import React, { useState, useEffect } from "react";
import { collection, getDocs, setDoc, doc, getDoc } from "firebase/firestore";
import { db } from "./firebaseConfig"; // Import the Firestore db instance

const TimesheetList = ({ weekDates }) => {
  const [employees, setEmployees] = useState([]);
  const [timesheetData, setTimesheetData] = useState({});
  const [modalOpen, setModalOpen] = useState(false); // State for opening the modal
  const [selectedEmployee, setSelectedEmployee] = useState(null); // Selected employee for time entry
  const [selectedDate, setSelectedDate] = useState(null); // Selected date for time entry
  const [startTime, setStartTime] = useState(""); // Start time input
  const [endTime, setEndTime] = useState(""); // End time input
  const [totalTime, setTotalTime] = useState(0); // Total time calculated

  // Fetch employees data from Firebase
  useEffect(() => {
    const fetchEmployees = async () => {
      try {
        const querySnapshot = await getDocs(collection(db, "employees"));
        const employeeData = querySnapshot.docs.map(doc => ({
          id: doc.id,
          ...doc.data()
        }));
        setEmployees(employeeData);
      } catch (error) {
        console.error("Error fetching employees:", error);
      }
    };

    fetchEmployees();
  }, []);

  // Initialize timesheet data when employees or weekDates change
  useEffect(() => {
    if (employees.length > 0 && weekDates.length > 0) {
      const initialTimesheetData = employees.reduce((acc, employee) => {
        acc[employee.id] = weekDates.reduce((weekAcc, date) => {
          weekAcc[date] = { startTime: "", endTime: "", normalWorked: 0, overtimeWorked: 0 }; // Initially empty values
          return weekAcc;
        }, {});
        return acc;
      }, {});
      setTimesheetData(initialTimesheetData);
      fetchTimesheetData(); // Fetch timesheet data when employees or weekDates change
    }
  }, [employees, weekDates]);

  // Function to calculate normal time and overtime
  const calculateTimeDetails = (start, end) => {
    const startParts = start.split(":").map(Number);
    const endParts = end.split(":").map(Number);

    // Create Date objects for accurate time calculations
    const startDate = new Date();
    startDate.setHours(startParts[0], startParts[1], 0);

    const endDate = new Date();
    endDate.setHours(endParts[0], endParts[1], 0);

    // If end time is earlier than start time, assume it’s the next day
    if (endDate < startDate) {
      endDate.setDate(endDate.getDate() + 1); // Move end date to the next day
    }

    // Calculate the total time difference in hours
    const diff = (endDate - startDate) / (1000 * 60 * 60);

    // Calculate normal working time (e.g., 9 AM to 5 PM)
    const normalStart = 9; // 9 AM
    const normalEnd = 17; // 5 PM

    let normalWorked = 0;
    let overtimeWorked = 0;

    // If the shift starts before 9 AM
    if (startDate.getHours() < normalStart) {
      startDate.setHours(normalStart, 0); // Normalize to 9 AM
    }

    // If the shift ends after 5 PM, consider overtime
    if (endDate.getHours() > normalEnd) {
      overtimeWorked = (endDate - new Date(endDate.setHours(normalEnd))) / (1000 * 60 * 60);
      normalWorked = diff - overtimeWorked;
    } else {
      normalWorked = diff;
    }

    return {
      normalWorked: normalWorked.toFixed(2), // Round to 2 decimal places
      overtimeWorked: overtimeWorked.toFixed(2) // Round to 2 decimal places
    };
  };

  // Handle input changes for work hours
  const handleInputChange = async (employeeId, date, start, end) => {
    const { normalWorked, overtimeWorked } = calculateTimeDetails(start, end);

    setTimesheetData((prevData) => {
      const newData = { ...prevData };
      newData[employeeId][date] = { startTime: start, endTime: end, normalWorked, overtimeWorked };
      return newData;
    });

    await saveTimesheetDataToFirebase(employeeId, date, start, end, normalWorked, overtimeWorked);
  };

  // Function to save timesheet data to Firebase
  const saveTimesheetDataToFirebase = async (employeeId, date, start, end, normalWorked, overtimeWorked) => {
    try {
      const timesheetDocRef = doc(db, "timesheets", employeeId); 
      const timesheetData = await getDoc(timesheetDocRef);

      let updatedData = {};

      if (timesheetData.exists()) {
        updatedData = timesheetData.data().hours;
        updatedData[date] = { startTime: start, endTime: end, normalWorked, overtimeWorked };
      } else {
        updatedData = { [date]: { startTime: start, endTime: end, normalWorked, overtimeWorked } };
      }

      await setDoc(timesheetDocRef, { hours: updatedData });
      fetchTimesheetData(); // Refetch the data after saving
    } catch (error) {
      console.error("Error saving timesheet data:", error);
    }
  };

  // Function to fetch the latest timesheet data
  const fetchTimesheetData = async () => {
    try {
      const querySnapshot = await getDocs(collection(db, "timesheets"));
      const updatedTimesheetData = {};

      querySnapshot.forEach((doc) => {
        const data = doc.data();
        updatedTimesheetData[doc.id] = data.hours;
      });

      setTimesheetData(updatedTimesheetData); // Update state with the fetched data
    } catch (error) {
      console.error("Error fetching timesheet data:", error);
    }
  };

  // Function to calculate the total worked hours for an employee over the entire month
  const calculateMonthlyTotal = (employeeId) => {
    let totalNormal = 0;
    let totalOvertime = 0;

    weekDates.forEach((date) => {
      if (timesheetData[employeeId] && timesheetData[employeeId][date]) {
        totalNormal += parseFloat(timesheetData[employeeId][date].normalWorked);
        totalOvertime += parseFloat(timesheetData[employeeId][date].overtimeWorked);
      }
    });

    const total = totalNormal + totalOvertime;
    return total.toFixed(2); // Rounded to 2 decimal places
  };

  // Function to calculate the total worked time when both start and end times are entered
  const calculateTotalTime = (start, end) => {
    const { normalWorked, overtimeWorked } = calculateTimeDetails(start, end);
    setTotalTime(parseFloat(normalWorked) + parseFloat(overtimeWorked)); // Update total time in state
  };

  return (
    <div>
      <table border="1">
        <thead>
          <tr>
            <th>Employee</th>
            {weekDates.map((date) => (
              <th key={date}>{date}</th>
            ))}
            <th>Total Worked Hours (Month)</th>
          </tr>
        </thead>
        <tbody>
          {employees.map((employee) => (
            <tr key={employee.id}>
              <td>{employee.name}</td> {/* Display employee name */}
              {weekDates.map((date) => (
                <td key={date}>
                  <button onClick={() => setSelectedEmployee(employee.id) && setSelectedDate(date) && setModalOpen(true)}>
                    Set Time
                  </button>
                  <div>
                    {timesheetData[employee.id]?.[date] && (
                      <div>
                        <span>{`From ${timesheetData[employee.id][date].startTime} - ${timesheetData[employee.id][date].endTime}`}</span>
                        <br />
                        <strong>{`Normal: ${timesheetData[employee.id][date].normalWorked} hours, Overtime: ${timesheetData[employee.id][date].overtimeWorked} hours`}</strong>
                      </div>
                    )}
                  </div>
                </td>
              ))}
              <td>{calculateMonthlyTotal(employee.id)} hours</td>
            </tr>
          ))}
        </tbody>
      </table>

      {/* Modal for setting time */}
      {modalOpen && (
        <div className="modal">
          <div className="modal-content">
            <h2>Set Time for {selectedEmployee}</h2>
            <div>
              <label>Start Time:</label>
              <input type="time" value={startTime} onChange={(e) => setStartTime(e.target.value)} />
            </div>
            <div>
              <label>End Time:</label>
              <input type="time" value={endTime} onChange={(e) => setEndTime(e.target.value)} />
            </div>
            <div>
              <button onClick={() => handleInputChange(selectedEmployee, selectedDate, startTime, endTime)}>Submit</button>
            </div>
            <div>
              <button onClick={() => setModalOpen(false)}>Cancel</button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default TimesheetList;
