import React, { useState, useEffect } from "react";
import { collection, getDocs, updateDoc, doc, setDoc, getDoc } from "firebase/firestore";
import { db } from "./firebaseConfig"; // Import the Firestore db instance

const TimesheetList = ({ weekDates }) => {
  const [employees, setEmployees] = useState([]);
  const [timesheetData, setTimesheetData] = useState({});
  const [modalOpen, setModalOpen] = useState(false); // State for opening the modal
  const [selectedEmployee, setSelectedEmployee] = useState(null); // Selected employee for time entry
  const [selectedDate, setSelectedDate] = useState(null); // Selected date for time entry
  const [startTime, setStartTime] = useState(""); // Start time input
  const [endTime, setEndTime] = useState(""); // End time input
  const [totalTime, setTotalTime] = useState(0); // Total time calculated
  const [overtime, setOvertime] = useState(0); // Overtime calculated
  const [timeAdjustments, setTimeAdjustments] = useState({}); // Time adjustment settings

  // Fetch employees data from Firebase
  useEffect(() => {
    const fetchEmployees = async () => {
      try {
        const querySnapshot = await getDocs(collection(db, "employees"));
        const employeeData = querySnapshot.docs.map(doc => ({
          id: doc.id,
          ...doc.data()
        }));
        setEmployees(employeeData);
      } catch (error) {
        console.error("Error fetching employees:", error);
      }
    };

    fetchEmployees();
  }, []);

  // Fetch time adjustments settings from Firebase
  useEffect(() => {
    const fetchTimeAdjustments = async () => {
      try {
        const timeAdjustmentsDocRef = doc(db, "timeAdjustments", "default");
        const timeAdjustmentsDoc = await getDoc(timeAdjustmentsDocRef);
        if (timeAdjustmentsDoc.exists()) {
          setTimeAdjustments(timeAdjustmentsDoc.data());
        }
      } catch (error) {
        console.error("Error fetching time adjustments:", error);
      }
    };

    fetchTimeAdjustments();
  }, []);

  // Initialize timesheet data when employees or weekDates change
  useEffect(() => {
    if (employees.length > 0 && weekDates.length > 0) {
      const initialTimesheetData = employees.reduce((acc, employee) => {
        acc[employee.id] = weekDates.reduce((weekAcc, date) => {
          weekAcc[date] = { startTime: "", endTime: "", totalTime: 0, overtime: 0 }; // Initially empty values
          return weekAcc;
        }, {});
        return acc;
      }, {});
      setTimesheetData(initialTimesheetData);
      fetchTimesheetData(); // Fetch timesheet data when employees or weekDates change
    }
  }, [employees, weekDates]);

  // Function to calculate the total time and overtime, handling overnight shifts
  const calculateTotalTime = (start, end) => {
    const startParts = start.split(":").map(Number);
    const endParts = end.split(":").map(Number);

    // Create Date objects for accurate time calculations
    const startDate = new Date();
    startDate.setHours(startParts[0], startParts[1], 0);

    const endDate = new Date();
    endDate.setHours(endParts[0], endParts[1], 0);

    // If end time is earlier than start time, assume it’s the next day
    if (endDate < startDate) {
      endDate.setDate(endDate.getDate() + 1); // Move end date to the next day
    }

    // Calculate the total time in hours
    const diff = (endDate - startDate) / (1000 * 60 * 60);
    setTotalTime(diff.toFixed(2)); // Set the total time in hours

    // Calculate overtime if total time exceeds normal working hours
    const normalStartParts = timeAdjustments.normalStartTime.split(":").map(Number);
    const normalEndParts = timeAdjustments.normalEndTime.split(":").map(Number);

    const normalStartDate = new Date();
    normalStartDate.setHours(normalStartParts[0], normalStartParts[1], 0);

    const normalEndDate = new Date();
    normalEndDate.setHours(normalEndParts[0], normalEndParts[1], 0);

    // If the total time worked exceeds normal working hours, calculate overtime
    if (diff > (normalEndDate - normalStartDate) / (1000 * 60 * 60)) {
      const overtimeHours = diff - (normalEndDate - normalStartDate) / (1000 * 60 * 60);
      setOvertime(overtimeHours.toFixed(2));
    } else {
      setOvertime(0); // Reset overtime if no overtime is worked
    }
  };

  // Handle input changes for work hours
  const handleInputChange = async (employeeId, date, start, end, totalTime, overtime) => {
    setTimesheetData((prevData) => {
      const newData = { ...prevData };
      newData[employeeId][date] = { startTime: start, endTime: end, totalTime, overtime };
      return newData;
    });

    await saveTimesheetDataToFirebase(employeeId, date, start, end, totalTime, overtime);
  };

  // Function to save timesheet data to Firebase
  const saveTimesheetDataToFirebase = async (employeeId, date, start, end, totalTime, overtime) => {
    try {
      const timesheetDocRef = doc(db, "timesheets", employeeId); 
      const timesheetData = await getDoc(timesheetDocRef);

      let updatedData = {};

      if (timesheetData.exists()) {
        updatedData = timesheetData.data().hours;
        updatedData[date] = { startTime: start, endTime: end, totalTime, overtime };
      } else {
        updatedData = { [date]: { startTime: start, endTime: end, totalTime, overtime } };
      }

      await setDoc(timesheetDocRef, { hours: updatedData });
      fetchTimesheetData(); // Refetch the data after saving
    } catch (error) {
      console.error("Error saving timesheet data:", error);
    }
  };

  // Function to fetch the latest timesheet data
  const fetchTimesheetData = async () => {
    try {
      const querySnapshot = await getDocs(collection(db, "timesheets"));
      const updatedTimesheetData = {};

      querySnapshot.forEach((doc) => {
        const data = doc.data();
        updatedTimesheetData[doc.id] = data.hours;
      });

      setTimesheetData(updatedTimesheetData); // Update state with the fetched data
    } catch (error) {
      console.error("Error fetching timesheet data:", error);
    }
  };

  // Handle opening the time modal and selecting time
  const handleSetTime = (employeeId, date) => {
    setSelectedEmployee(employeeId);
    setSelectedDate(date);
    setModalOpen(true);
  };

  // Handle closing the time modal
  const handleCloseModal = () => {
    setModalOpen(false);
    setStartTime("");
    setEndTime("");
    setTotalTime(0);
    setOvertime(0);
  };

  return (
    <div>
      <table border="1">
        <thead>
          <tr>
            <th>Employee</th>
            {weekDates.map((date) => (
              <th key={date}>{date}</th>
            ))}
          </tr>
        </thead>
        <tbody>
          {employees.map((employee) => (
            <tr key={employee.id}>
              <td>{employee.name}</td> {/* Removed the input field for name */}
              {weekDates.map((date) => (
                <td key={date}>
                  <button onClick={() => handleSetTime(employee.id, date)}>Set Time</button>
                  <div>
                    {timesheetData[employee.id]?.[date] && (
                      <div>
                        <span>{`From ${timesheetData[employee.id][date].startTime} - ${timesheetData[employee.id][date].endTime}`}</span>
                        <span>{`Total Time: ${timesheetData[employee.id][date].totalTime} hours`}</span>
                        <span>{`Overtime: ${timesheetData[employee.id][date].overtime} hours`}</span>
                      </div>
                    )}
                  </div>
                </td>
              ))}
            </tr>
          ))}
        </tbody>
      </table>

      {modalOpen && (
        <div className="modal">
          <h2>Set Time for {selectedEmployee} on {selectedDate}</h2>
          <input 
            type="time" 
            value={startTime} 
            onChange={(e) => setStartTime(e.target.value)} 
          />
          <input 
            type="time" 
            value={endTime} 
            onChange={(e) => setEndTime(e.target.value)} 
          />
          <button onClick={() => calculateTotalTime(startTime, endTime)}>
            Calculate Time
          </button>
          <div>
            <span>Total Time: {totalTime} hours</span>
            <span>Overtime: {overtime} hours</span>
          </div>
          <button onClick={() => handleInputChange(selectedEmployee, selectedDate, startTime, endTime, totalTime, overtime)}>Save</button>
          <button onClick={handleCloseModal}>Cancel</button>
        </div>
      )}
    </div>
  );
};

export default TimesheetList;
