import { useState, useEffect } from "react";
import { collection, getDocs, getDoc, doc, setDoc } from "firebase/firestore"; // Add setDoc import here
import { db } from "./firebaseConfig"; // Import the Firestore db instance

const TimesheetList = ({ weekDates }) => {
  const [employees, setEmployees] = useState([]);
  const [timesheetData, setTimesheetData] = useState({});
  const [timeAdjustments, setTimeAdjustments] = useState({}); // State for time adjustments
  const [modalOpen, setModalOpen] = useState(false); // State for opening the modal
  const [selectedEmployee, setSelectedEmployee] = useState(null); // Selected employee for time entry
  const [selectedDate, setSelectedDate] = useState(null); // Selected date for time entry
  const [startTime, setStartTime] = useState(""); // Start time input
  const [endTime, setEndTime] = useState(""); // End time input
  const [totalTime, setTotalTime] = useState(0); // Total time calculated
  const [normalTime, setNormalTime] = useState(0); // Normal time
  const [overtime, setOvertime] = useState(0); // Overtime

  // Fetch employees data and time adjustments from Firebase
  useEffect(() => {
    const fetchData = async () => {
      try {
        // Fetch employees
        const employeeSnapshot = await getDocs(collection(db, "employees"));
        const employeeData = employeeSnapshot.docs.map(doc => ({
          id: doc.id,
          ...doc.data()
        }));
        setEmployees(employeeData);

        // Fetch time adjustments
        const settingsDocRef = doc(db, "timeAdjustments", "settings"); // Correct Firestore path
        const settingsDoc = await getDoc(settingsDocRef);
        if (settingsDoc.exists()) {
          setTimeAdjustments(settingsDoc.data()); // Set the adjustments to the state
        } else {
          console.log("No time adjustments found");
        }
      } catch (error) {
        console.error("Error fetching data:", error);
      }
    };

    fetchData();
  }, []); // Empty dependency array means this effect runs only once on component mount

  // Initialize timesheet data when employees or weekDates change
  useEffect(() => {
    if (employees.length > 0 && weekDates.length > 0) {
      const initialTimesheetData = employees.reduce((acc, employee) => {
        acc[employee.id] = weekDates.reduce((weekAcc, date) => {
          weekAcc[date] = { startTime: "", endTime: "", totalTime: 0 }; // Initially empty values
          return weekAcc;
        }, {});
        return acc;
      }, {});
      setTimesheetData(initialTimesheetData);
      fetchTimesheetData(); // Fetch timesheet data when employees or weekDates change
    }
  }, [employees, weekDates]);

  // Function to calculate the total time, normal time, and overtime
  const calculateTotalTime = (start, end) => {
    const startParts = start.split(":").map(Number);
    const endParts = end.split(":").map(Number);

    // Create Date objects for accurate time calculations
    const startDate = new Date();
    startDate.setHours(startParts[0], startParts[1], 0);

    const endDate = new Date();
    endDate.setHours(endParts[0], endParts[1], 0);

    // If end time is earlier than start time, assume it’s the next day
    if (endDate < startDate) {
      endDate.setDate(endDate.getDate() + 1); // Move end date to the next day
    }

    // Calculate the total time worked in hours
    const diff = (endDate - startDate) / (1000 * 60 * 60);
    setTotalTime(diff.toFixed(2)); // Set the total time in hours
  };


  // Handle input changes for work hours
  const handleInputChange = async (employeeId, date, start, end, totalTime, normalTime, overtime) => {
    setTimesheetData((prevData) => {
      const newData = { ...prevData };
      newData[employeeId][date] = { startTime: start, endTime: end, totalTime, normalTime, overtime };
      return newData;
    });

    await saveTimesheetDataToFirebase(employeeId, date, start, end, totalTime, normalTime, overtime);
  };

  // Function to save timesheet data to Firebase
  const saveTimesheetDataToFirebase = async (employeeId, date, start, end, totalTime, normalTime, overtime) => {
    try {
      const timesheetDocRef = doc(db, "timesheets", employeeId); 
      const timesheetData = await getDoc(timesheetDocRef);

      let updatedData = {};

      if (timesheetData.exists()) {
        updatedData = timesheetData.data().hours;
        updatedData[date] = { startTime: start, endTime: end, totalTime, normalTime, overtime };
      } else {
        updatedData = { [date]: { startTime: start, endTime: end, totalTime, normalTime, overtime } };
      }

      await setDoc(timesheetDocRef, { hours: updatedData });
      fetchTimesheetData(); // Refetch the data after saving
    } catch (error) {
      console.error("Error saving timesheet data:", error);
    }
  };

  // Function to fetch the latest timesheet data
  const fetchTimesheetData = async () => {
    try {
      const querySnapshot = await getDocs(collection(db, "timesheets"));
      const updatedTimesheetData = {};

      querySnapshot.forEach((doc) => {
        const data = doc.data();
        updatedTimesheetData[doc.id] = data.hours;
      });

      setTimesheetData(updatedTimesheetData); // Update state with the fetched data
    } catch (error) {
      console.error("Error fetching timesheet data:", error);
    }
  };

  // Handle opening the time modal and selecting time
  const handleSetTime = (employeeId, date) => {
    setSelectedEmployee(employeeId);
    setSelectedDate(date);
    setModalOpen(true);
  };

  // Handle closing the time modal
  const handleCloseModal = () => {
    setModalOpen(false);
    setStartTime("");
    setEndTime("");
    setTotalTime(0);
    setNormalTime(0);
    setOvertime(0);
  };

  return (
    <div>
      {/* Display Time Adjustments */}
      <div>
        <h3>Time Adjustments</h3>
        <p>Normal Start Time: {timeAdjustments.normalStartTime}</p>
        <p>Normal End Time: {timeAdjustments.normalEndTime}</p>
        <p>Overtime Start Time: {timeAdjustments.overtimeStartTime}</p>
        <p>Overtime End Time: {timeAdjustments.overtimeEndTime}</p>
      </div>

      <table border="1">
        <thead>
          <tr>
            <th>Employee</th>
            {weekDates.map((date) => (
              <th key={date}>{date}</th>
            ))}
          </tr>
        </thead>
        <tbody>
          {employees.map((employee) => (
            <tr key={employee.id}>
              <td>{employee.name}</td> {/* Removed the input field for name */}
              {weekDates.map((date) => (
                <td key={date}>
                  {timesheetData[employee.id] && timesheetData[employee.id][date] ? (
                    <>
                      <div>{timesheetData[employee.id][date].startTime} - {timesheetData[employee.id][date].endTime}</div>
                      <div>Total: {timesheetData[employee.id][date].totalTime} hours</div>
                      <div>Normal: {timesheetData[employee.id][date].normalTime} hours</div>
                      <div>Overtime: {timesheetData[employee.id][date].overtime} hours</div>
                    </>
                  ) : (
                    <button onClick={() => handleSetTime(employee.id, date)}>Set Time</button>
                  )}
                </td>
              ))}
            </tr>
          ))}
        </tbody>
      </table>

      {modalOpen && (
        <div>
          <h3>Set Time for {employees.find(emp => emp.id === selectedEmployee)?.name} on {selectedDate}</h3>
          <input
            type="time"
            value={startTime}
            onChange={(e) => setStartTime(e.target.value)}
          />
          <input
            type="time"
            value={endTime}
            onChange={(e) => setEndTime(e.target.value)}
          />
          <button
            onClick={() => {
              calculateTotalTime(startTime, endTime);
              handleInputChange(
                selectedEmployee,
                selectedDate,
                startTime,
                endTime,
                totalTime,
                normalTime,
                overtime
              );
            }}
          >
            Save Time
          </button>
          <button onClick={handleCloseModal}>Cancel</button>
        </div>
      )}
    </div>
  );
};

export default TimesheetList;
