import React, { useState } from "react";

const TimesheetList = ({ weekDates }) => {
  const employees = [
    { name: "Alice", id: 1 },
    { name: "Bob", id: 2 },
    { name: "Charlie", id: 3 },
  ];

  const [timesheetData, setTimesheetData] = useState(
    employees.reduce((acc, employee) => {
      acc[employee.id] = weekDates.reduce((weekAcc, date) => {
        weekAcc[date] = ""; // Initially no work hours
        return weekAcc;
      }, {});
      return acc;
    }, {})
  );

  const handleInputChange = (employeeId, date, hours) => {
    setTimesheetData((prevData) => {
      const newData = { ...prevData };
      newData[employeeId][date] = hours;
      return newData;
    });
  };

  return (
    <div>
      <table border="1">
        <thead>
          <tr>
            <th>Employee</th>
            {weekDates.map((date) => (
              <th key={date}>{date}</th>
            ))}
          </tr>
        </thead>
        <tbody>
          {employees.map((employee) => (
            <tr key={employee.id}>
              <td>{employee.name}</td>
              {weekDates.map((date) => (
                <td key={date}>
                  <input
                    type="number"
                    value={timesheetData[employee.id][date]}
                    onChange={(e) =>
                      handleInputChange(
                        employee.id,
                        date,
                        e.target.value
                      )
                    }
                  />
                </td>
              ))}
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default TimesheetList;
