import React, { useState } from "react";
import EmployeeManagement from "./EmployeeManagement";
import TimesheetList from "./TimesheetList";

const App = () => {
  const [activeView, setActiveView] = useState("timesheet"); // Default view is "timesheet"

  const handleSidebarClick = (view) => {
    setActiveView(view);
  };

  return (
    <div className="app-container">
      {/* Sidebar */}
      <div className="sidebar">
        <h2>Admin Dashboard</h2>
        <button onClick={() => handleSidebarClick("timesheet")}>Timesheet</button>
        <button onClick={() => handleSidebarClick("employees")}>Employee Management</button>
      </div>

      {/* Main Content */}
      <div className="main-content">
        {activeView === "timesheet" && <TimesheetList />}
        {activeView === "employees" && <EmployeeManagement />}
      </div>
    </div>
  );
};

export default App;
