import React, { useEffect } from "react";
import "./login.css";

const Login = ({ onLogin }) => {
  useEffect(() => {
    const container = document.querySelector('.mongolia-background');
    const numLines = 6;
    const numStars = 20;
    const particles = [];

    // Create gold lines
    for (let i = 0; i < numLines; i++) {
      const line = document.createElement('div');
      line.classList.add('gold-line');
      line.style.left = `${Math.random() * window.innerWidth}px`;
      line.style.top = `${Math.random() * window.innerHeight}px`;
      container.appendChild(line);
      particles.push(line);
    }

    // Create stars
    for (let i = 0; i < numStars; i++) {
      const star = document.createElement('div');
      star.classList.add('star');
      star.style.left = `${Math.random() * window.innerWidth}px`;
      star.style.top = `${Math.random() * window.innerHeight}px`;
      container.appendChild(star);
      particles.push(star);
    }

    // Mouse move effect
    const handleMouseMove = (event) => {
      particles.forEach((particle) => {
        // Get particle's initial position
        const rect = particle.getBoundingClientRect();
        const particleX = rect.left + rect.width / 2;
        const particleY = rect.top + rect.height / 2;

        // Calculate the distance from cursor to the particle
        const dx = event.clientX - particleX;
        const dy = event.clientY - particleY;
        const dist = Math.sqrt(dx * dx + dy * dy);

        // Calculate movement intensity based on proximity to cursor
        const maxMoveDistance = 15; // max distance a particle can move
        const moveFactor = Math.min(1, 100 / dist); // closer particles move more
        const moveX = dx * moveFactor * maxMoveDistance / dist;
        const moveY = dy * moveFactor * maxMoveDistance / dist;

        particle.style.transform = `translate(${moveX}px, ${moveY}px)`;
      });
    };

    document.addEventListener("mousemove", handleMouseMove);

    return () => {
      document.removeEventListener("mousemove", handleMouseMove);
    };
  }, []);

  const [username, setUsername] = React.useState("");
  const [password, setPassword] = React.useState("");

  const handleSubmit = (e) => {
    e.preventDefault();
    onLogin(username, password);
  };

  return (
    <div className="login-container">
      <div className="mongolia-background"></div>
      <div className="login-box">
        <h2 className="login-title">Login</h2>
        <form onSubmit={handleSubmit}>
          <div className="input-group">
            <label htmlFor="username">Username</label>
            <input
              type="text"
              id="username"
              value={username}
              onChange={(e) => setUsername(e.target.value)}
              placeholder="Enter your username"
              required
            />
          </div>
          <div className="input-group">
            <label htmlFor="password">Password</label>
            <input
              type="password"
              id="password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              placeholder="Enter your password"
              required
            />
          </div>
          <button type="submit" className="login-btn">Login</button>
        </form>
      </div>
    </div>
  );
};

export default Login;
