import { useState, useEffect } from "react";
import { collection, onSnapshot, doc, setDoc, getDoc } from "firebase/firestore";
import { db } from "./firebaseConfig";

const TimesheetList = ({ weekDates }) => {
  const [employees, setEmployees] = useState([]);
  const [timesheetData, setTimesheetData] = useState({});
  const [timeAdjustments, setTimeAdjustments] = useState({});
  const [modalOpen, setModalOpen] = useState(false);
  const [selectedEmployee, setSelectedEmployee] = useState(null);
  const [selectedDate, setSelectedDate] = useState(null);
  const [startTime, setStartTime] = useState("");
  const [endTime, setEndTime] = useState("");
  const [nextDay, setNextDay] = useState(false);
  const [isHoliday, setIsHoliday] = useState(false);
  const [totalTime, setTotalTime] = useState(0);
  const [nightShiftHours, setNightShiftHours] = useState(0);
  const [holidayTime, setHolidayTime] = useState(0);

  // Fetch employees and timesheet data on component mount
  useEffect(() => {
    const fetchEmployees = () => {
      const unsubscribe = onSnapshot(collection(db, "employees"), (snapshot) => {
        const employeeData = snapshot.docs.map((doc) => ({
          id: doc.id,
          ...doc.data(),
        }));
        setEmployees(employeeData);
      });

      return unsubscribe; // Cleanup on unmount
    };

    const fetchTimeAdjustments = () => {
      const settingsDocRef = doc(db, "timeAdjustments", "settings");
      onSnapshot(settingsDocRef, (docSnapshot) => {
        if (docSnapshot.exists()) {
          setTimeAdjustments(docSnapshot.data());
        } else {
          console.log("No time adjustments found");
        }
      });
    };

    fetchEmployees();
    fetchTimeAdjustments();
  }, []);

  // Fetch timesheet data in real-time
  useEffect(() => {
    const fetchTimesheetData = () => {
      const timesheetDataRef = collection(db, "timesheets");

      const unsubscribe = onSnapshot(timesheetDataRef, (snapshot) => {
        const updatedTimesheetData = {};

        snapshot.forEach((doc) => {
          const data = doc.data();
          const employeeId = doc.id;

          if (data.hours) {
            updatedTimesheetData[employeeId] = {};
            for (const [date, record] of Object.entries(data.hours)) {
              updatedTimesheetData[employeeId][date] = {
                startTime: record.startTime,
                endTime: record.endTime,
                totalTime: parseFloat(record.totalTime),
                nightShift: parseFloat(record.nightShift),
                holidayTime: parseFloat(record.holidayTime),
                isHoliday: Boolean(record.isHoliday),
              };
            }
          }
        });

        setTimesheetData(updatedTimesheetData);
      });

      return unsubscribe; // Cleanup on unmount
    };

    fetchTimesheetData();
  }, []);

  const handleSetTime = (employeeId, date) => {
    setSelectedEmployee(employeeId);
    setSelectedDate(date);
    setModalOpen(true);
  };

  const handleStartTimeChange = (event) => {
    setStartTime(event.target.value);
    calculateTimes(event.target.value, endTime, nextDay);
  };

  const handleEndTimeChange = (event) => {
    setEndTime(event.target.value);
    calculateTimes(startTime, event.target.value, nextDay);
  };

  const handleNextDayToggle = () => {
    setNextDay((prev) => !prev);
    calculateTimes(startTime, endTime, !nextDay);
  };

  const handleHolidayToggle = () => {
    setIsHoliday((prev) => !prev);
    if (!isHoliday) {
      setHolidayTime(totalTime);
      setTotalTime(0);
      setNightShiftHours(0);
    } else {
      setHolidayTime(0);
      calculateTimes(startTime, endTime, nextDay);
    }
  };

  const handleCloseModal = () => {
    setModalOpen(false);
  };

  const calculateTimes = (start, end, isNextDay) => {
    if (!start || !end) return;

    const [startHours, startMinutes] = start.split(":").map(Number);
    const [endHours, endMinutes] = end.split(":").map(Number);

    const startDate = new Date();
    startDate.setHours(startHours, startMinutes, 0);

    const endDate = new Date();
    endDate.setHours(endHours, endMinutes, 0);

    if (isNextDay) endDate.setDate(endDate.getDate() + 1);

    const totalTimeInHours = (endDate - startDate) / (1000 * 60 * 60);

    const nightShiftStart = new Date(startDate);
    nightShiftStart.setHours(22, 0, 0);

    const nightShiftEnd = new Date(startDate);
    nightShiftEnd.setDate(nightShiftEnd.getDate() + 1);
    nightShiftEnd.setHours(6, 0, 0);

    let nightShiftHours = 0;

    if (startDate < nightShiftEnd && endDate >= nightShiftStart) {
      const nightShiftStartTime = startDate < nightShiftStart ? nightShiftStart : startDate;
      const nightShiftEndTime = endDate > nightShiftEnd ? nightShiftEnd : endDate;
      nightShiftHours = (nightShiftEndTime - nightShiftStartTime) / (1000 * 60 * 60);
    }

    setTotalTime(totalTimeInHours.toFixed(2));
    setNightShiftHours(nightShiftHours.toFixed(2));
  };

  const saveTimesheetDataToFirebase = async (employeeId, date, start, end, totalTime, nightShift, holidayTime) => {
    try {
      const timesheetDocRef = doc(db, "timesheets", employeeId);
      const timesheetData = (await getDoc(timesheetDocRef)).data()?.hours || {};

      timesheetData[date] = {
        startTime: start,
        endTime: end,
        totalTime,
        nightShift,
        holidayTime,
        isHoliday,
      };

      await setDoc(timesheetDocRef, { hours: timesheetData });
    } catch (error) {
      console.error("Error saving timesheet data:", error);
    }
  };

  const handleInputChange = async (employeeId, date, start, end, totalTime, nightShift, holidayTime) => {
    setTimesheetData((prevData) => {
      const newData = { ...prevData };
      newData[employeeId][date] = { startTime: start, endTime: end, totalTime, nightShift, holidayTime, isHoliday };
      return newData;
    });

    await saveTimesheetDataToFirebase(employeeId, date, start, end, totalTime, nightShift, holidayTime);
  };

  return (
    <div>
      <table border="1">
        <thead>
          <tr>
            <th>Employee</th>
            {weekDates.map((date) => (
              <th key={date}>{date}</th>
            ))}
          </tr>
        </thead>
        <tbody>
          {employees.map((employee) => (
            <tr key={employee.id}>
              <td>{employee.name}</td>
              {weekDates.map((date) => (
                <td key={date}>
                  <button onClick={() => handleSetTime(employee.id, date)}>Set Time</button>
                  {timesheetData[employee.id]?.[date]?.totalTime && (
                    <div>
                      <p>Total Time: {timesheetData[employee.id][date].totalTime} hours</p>
                      <p>Night Shift: {timesheetData[employee.id][date].nightShift} hours</p>
                      <p>Holiday Time: {timesheetData[employee.id][date].holidayTime} hours</p>
                    </div>
                  )}
                </td>
              ))}
            </tr>
          ))}
        </tbody>
      </table>

      {modalOpen && (
        <div className="modal">
          <h2>Set Time for {selectedEmployee}</h2>
          <div>
            <label>Start Time: </label>
            <input type="time" value={startTime} onChange={handleStartTimeChange} />
          </div>
          <div>
            <label>End Time: </label>
            <input type="time" value={endTime} onChange={handleEndTimeChange} />
          </div>
          <div>
            <label>
              <input type="checkbox" checked={nextDay} onChange={handleNextDayToggle} />
              Next Day
            </label>
            <label>
              <input type="checkbox" checked={isHoliday} onChange={handleHolidayToggle} />
              Holiday
            </label>
          </div>
          <p>Total Time: {totalTime} hours</p>
          <p>Night Shift: {nightShiftHours} hours</p>
          <p>Holiday Time: {holidayTime} hours</p>
          <button
            onClick={() =>
              handleInputChange(selectedEmployee, selectedDate, startTime, endTime, totalTime, nightShiftHours, holidayTime)
            }
          >
            Save
          </button>
          <button onClick={handleCloseModal}>Close</button>
        </div>
      )}
    </div>
  );
};

export default TimesheetList;
