import React, { useState, useEffect } from "react";

// Simulated API calls to get and manage employee data
const fetchEmployees = async () => {
  // Replace with real API call to fetch employees from your database
  return [
    { id: 1, name: "Alice", email: "alice@example.com", position: "Developer" },
    { id: 2, name: "Bob", email: "bob@example.com", position: "Manager" },
    { id: 3, name: "Charlie", email: "charlie@example.com", position: "Designer" }
  ];
};

const EmployeeManagement = () => {
  const [employees, setEmployees] = useState([]);
  const [newEmployee, setNewEmployee] = useState({
    name: "",
    email: "",
    position: "",
  });

  useEffect(() => {
    const loadEmployees = async () => {
      const data = await fetchEmployees();
      setEmployees(data);
    };
    loadEmployees();
  }, []);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setNewEmployee((prevState) => ({
      ...prevState,
      [name]: value,
    }));
  };

  const handleAddEmployee = () => {
    setEmployees((prevState) => [
      ...prevState,
      { ...newEmployee, id: Date.now() }, // Simulating new ID generation
    ]);
    setNewEmployee({ name: "", email: "", position: "" }); // Reset form
  };

  const handleDeleteEmployee = (id) => {
    setEmployees((prevState) => prevState.filter((employee) => employee.id !== id));
  };

  return (
    <div style={{ display: "flex", flexDirection: "column", padding: "20px" }}>
      <h2>Employee Management</h2>

      <div style={{ marginBottom: "20px" }}>
        <h3>Add New Employee</h3>
        <input
          type="text"
          name="name"
          placeholder="Name"
          value={newEmployee.name}
          onChange={handleInputChange}
        />
        <input
          type="email"
          name="email"
          placeholder="Email"
          value={newEmployee.email}
          onChange={handleInputChange}
        />
        <input
          type="text"
          name="position"
          placeholder="Position"
          value={newEmployee.position}
          onChange={handleInputChange}
        />
        <button onClick={handleAddEmployee}>Add Employee</button>
      </div>

      <h3>Employee List</h3>
      <table border="1" style={{ width: "100%", marginTop: "20px" }}>
        <thead>
          <tr>
            <th>Name</th>
            <th>Email</th>
            <th>Position</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {employees.map((employee) => (
            <tr key={employee.id}>
              <td>{employee.name}</td>
              <td>{employee.email}</td>
              <td>{employee.position}</td>
              <td>
                <button onClick={() => handleDeleteEmployee(employee.id)}>Delete</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default EmployeeManagement;
