import React, { useState, useEffect } from "react";
import { collection, addDoc, getDocs, updateDoc, doc, deleteDoc } from "firebase/firestore";
import { db } from "./firebaseConfig"; // Import the Firestore db instance

const EmployeeManagement = () => {
  const [employees, setEmployees] = useState([]);
  const [newEmployee, setNewEmployee] = useState({
    name: "",
    phone: "",
    sap: "", // Added SAP field
  });
  const [editingEmployee, setEditingEmployee] = useState(null);
  const [isModalOpen, setIsModalOpen] = useState(false);

  // Fetch employees data from Firebase
  useEffect(() => {
    const fetchEmployees = async () => {
      try {
        // Get employees collection from Firebase
        const querySnapshot = await getDocs(collection(db, "employees"));
        const employeeData = querySnapshot.docs.map(doc => ({
          id: doc.id,
          ...doc.data(),
        }));
        setEmployees(employeeData);
      } catch (error) {
        console.error("Error fetching employees:", error);
      }
    };

    fetchEmployees();
  }, []);

  // Handle input change for new employee form
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setNewEmployee((prev) => ({
      ...prev,
      [name]: value,
    }));
  };

  // Handle adding a new employee to Firestore
  const handleAddEmployee = async (e) => {
    e.preventDefault();
    try {
      if (!newEmployee.name || !newEmployee.phone || !newEmployee.sap) {
        alert("Please fill all fields!");
        return;
      }
      // Add new employee to Firebase
      const docRef = await addDoc(collection(db, "employees"), newEmployee);
      setEmployees((prev) => [
        ...prev,
        { id: docRef.id, ...newEmployee },
      ]);
      setNewEmployee({ name: "", phone: "", sap: "" }); // Reset form after submission
      setIsModalOpen(false); // Close the modal after adding the employee
    } catch (error) {
      console.error("Error adding employee:", error);
    }
  };

  // Handle editing employee details (triggered by double click on the cell)
  const handleDoubleClick = (employee, field) => {
    setEditingEmployee({ ...employee, fieldToEdit: field });
    setIsModalOpen(true); // Open the modal for editing
  };

  const handleInputChangeInEditMode = (e) => {
    const { name, value } = e.target;
    setEditingEmployee((prev) => ({
      ...prev,
      [name]: value,
    }));
  };

  const handleUpdateEmployee = async () => {
    try {
      const employeeDoc = doc(db, "employees", editingEmployee.id);
      await updateDoc(employeeDoc, {
        name: editingEmployee.name,
        phone: editingEmployee.phone,
        sap: editingEmployee.sap, // Update SAP field
      });

      setEmployees((prev) =>
        prev.map((emp) =>
          emp.id === editingEmployee.id ? editingEmployee : emp
        )
      );
      setEditingEmployee(null); // Close edit mode after update
      setIsModalOpen(false); // Close modal after update
    } catch (error) {
      console.error("Error updating employee:", error);
    }
  };

  // Handle deleting an employee with a confirmation
  const handleDeleteEmployee = async (employeeId) => {
    const isConfirmed = window.confirm("Are you sure you want to delete this employee?");
    if (isConfirmed) {
      try {
        const employeeDoc = doc(db, "employees", employeeId);
        await deleteDoc(employeeDoc);
        setEmployees((prev) => prev.filter((emp) => emp.id !== employeeId));
      } catch (error) {
        console.error("Error deleting employee:", error);
      }
    }
  };

  return (
    <div>
      {/* Button to open modal for adding new employee */}
      <button onClick={() => setIsModalOpen(true)}>Add New Employee</button>

      {/* Display Employee List */}
      <h3>Employee List</h3>
      <table className="employee-table" border="1">
        <thead>
          <tr>
            <th>Name</th>
            <th>Phone</th>
            <th>SAP</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {employees.map((employee) => (
            <tr key={employee.id}>
              <td
                onDoubleClick={() => handleDoubleClick(employee, "name")}
              >
                {editingEmployee && editingEmployee.id === employee.id && editingEmployee.fieldToEdit === "name" ? (
                  <input
                    type="text"
                    name="name"
                    value={editingEmployee.name}
                    onChange={handleInputChangeInEditMode}
                  />
                ) : (
                  employee.name
                )}
              </td>
              <td
                onDoubleClick={() => handleDoubleClick(employee, "phone")}
              >
                {editingEmployee && editingEmployee.id === employee.id && editingEmployee.fieldToEdit === "phone" ? (
                  <input
                    type="text"
                    name="phone"
                    value={editingEmployee.phone}
                    onChange={handleInputChangeInEditMode}
                  />
                ) : (
                  employee.phone
                )}
              </td>
              <td
                onDoubleClick={() => handleDoubleClick(employee, "sap")}
              >
                {editingEmployee && editingEmployee.id === employee.id && editingEmployee.fieldToEdit === "sap" ? (
                  <input
                    type="number"
                    name="sap"
                    value={editingEmployee.sap}
                    onChange={handleInputChangeInEditMode}
                  />
                ) : (
                  employee.sap
                )}
              </td>
              <td>
              {editingEmployee && editingEmployee.id === employee.id ? (
                <button
                  style={{ padding: "10px 10px", fontSize: "16px", margin: "5px" }}
                  onClick={handleUpdateEmployee}
                >
                  Save
                </button>
              ) : (
                <button
                  style={{ padding: "10px 20px", fontSize: "16px", margin: "5px" }}
                  onClick={() => handleDoubleClick(employee, "name")}
                >
                  Edit
                </button>
              )}
              <button
                style={{ padding: "10px 20px", fontSize: "16px", margin: "5px" }}
                onClick={() => handleDeleteEmployee(employee.id)}
              >
                Delete
              </button>
            </td>

            </tr>
          ))}
        </tbody>
      </table>

      {/* Modal for adding or editing employee */}
      {/* Modal for adding or editing employee */}
{isModalOpen && (
  <div className="modal">
    <div className="modal-content">
      <h2>{editingEmployee ? "Edit Employee" : "Add New Employee"}</h2>
      <label>Name</label>
      <input
        type="text"
        value={editingEmployee?.name || newEmployee.name}
        onChange={(e) =>
          editingEmployee
            ? setEditingEmployee({ ...editingEmployee, name: e.target.value })
            : setNewEmployee({ ...newEmployee, name: e.target.value })
        }
      />
      <label>Phone</label>
      <input
        type="text"
        value={editingEmployee?.phone || newEmployee.phone}
        onChange={(e) =>
          editingEmployee
            ? setEditingEmployee({ ...editingEmployee, phone: e.target.value })
            : setNewEmployee({ ...newEmployee, phone: e.target.value })
        }
      />
      <label>SAP</label>
      <input
        type="number"
        value={editingEmployee?.sap || newEmployee.sap}
        onChange={(e) =>
          editingEmployee
            ? setEditingEmployee({ ...editingEmployee, sap: e.target.value })
            : setNewEmployee({ ...newEmployee, sap: e.target.value })
        }
      />
      <div className="modal-actions">
        <button className="save-btn" onClick={editingEmployee ? handleUpdateEmployee : handleAddEmployee}>
          {editingEmployee ? "Save" : "Add Employee"}
        </button>
        <button className="close-btn" onClick={() => setIsModalOpen(false)}>Close</button>
      </div>
    </div>
  </div>
)}

    </div>
  );
};

export default EmployeeManagement;
