import React, { useEffect, useRef, useState } from "react";
import "./login.css";

const Login = ({ onLogin }) => {
  const canvasRef = useRef(null);
  const [particles, setParticles] = useState([]);
  const numParticles = 30;

  useEffect(() => {
    const canvas = canvasRef.current;
    const context = canvas.getContext("2d");
    canvas.width = window.innerWidth;
    canvas.height = window.innerHeight;

    // Create particles with random positions
    const particlesArray = [];
    for (let i = 0; i < numParticles; i++) {
      particlesArray.push({
        x: Math.random() * canvas.width,
        y: Math.random() * canvas.height,
        size: 3,
        originalX: Math.random() * canvas.width,
        originalY: Math.random() * canvas.height,
      });
    }
    setParticles(particlesArray);

    const drawParticles = () => {
      context.clearRect(0, 0, canvas.width, canvas.height);

      // Draw each particle
      particles.forEach((particle) => {
        context.beginPath();
        context.arc(particle.x, particle.y, particle.size, 0, Math.PI * 2);
        context.fillStyle = "#ffd700";
        context.fill();
      });

      // Draw lines between close particles to create a web effect
      for (let i = 0; i < particles.length; i++) {
        for (let j = i + 1; j < particles.length; j++) {
          const dx = particles[i].x - particles[j].x;
          const dy = particles[i].y - particles[j].y;
          const distance = Math.sqrt(dx * dx + dy * dy);

          if (distance < 100) {
            context.beginPath();
            context.moveTo(particles[i].x, particles[i].y);
            context.lineTo(particles[j].x, particles[j].y);
            context.strokeStyle = `rgba(255, 215, 0, ${1 - distance / 100})`;
            context.lineWidth = 0.5;
            context.stroke();
          }
        }
      }
    };

    const updateParticles = (e) => {
      particles.forEach((particle) => {
        const dx = e.clientX - particle.originalX;
        const dy = e.clientY - particle.originalY;
        const dist = Math.sqrt(dx * dx + dy * dy);
        const maxDist = 100;
        const pullStrength = Math.min(1, maxDist / dist);

        particle.x = particle.originalX + dx * pullStrength * 0.1;
        particle.y = particle.originalY + dy * pullStrength * 0.1;
      });
      drawParticles();
    };

    drawParticles();

    window.addEventListener("mousemove", updateParticles);
    window.addEventListener("resize", () => {
      canvas.width = window.innerWidth;
      canvas.height = window.innerHeight;
      drawParticles();
    });

    return () => {
      window.removeEventListener("mousemove", updateParticles);
    };
  }, [particles]);

  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");

  const handleSubmit = (e) => {
    e.preventDefault();
    onLogin(username, password);
  };

  return (
    <div className="login-container">
      <canvas ref={canvasRef} className="mongolia-background"></canvas>
      <div className="login-box">
        <h2 className="login-title">Login</h2>
        <form onSubmit={handleSubmit}>
          <div className="input-group">
            <label htmlFor="username">Username</label>
            <input
              type="text"
              id="username"
              value={username}
              onChange={(e) => setUsername(e.target.value)}
              placeholder="Enter your username"
              required
            />
          </div>
          <div className="input-group">
            <label htmlFor="password">Password</label>
            <input
              type="password"
              id="password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              placeholder="Enter your password"
              required
            />
          </div>
          <button type="submit" className="login-btn">Login</button>
        </form>
      </div>
    </div>
  );
};

export default Login;
