import React, { useState, useEffect } from "react";
// Import Firebase functions and Firestore
import { collection, addDoc, getDocs, updateDoc, doc, setDoc, getDoc } from "firebase/firestore";
import { db } from "./firebaseConfig"; // Import the Firestore db instance

const TimesheetList = ({ weekDates }) => {
  const [employees, setEmployees] = useState([]);
  const [timesheetData, setTimesheetData] = useState({});
  const [newEmployeeName, setNewEmployeeName] = useState(""); // State for new employee name input

  // Fetch employees data from Firebase
  useEffect(() => {
    const fetchEmployees = async () => {
      try {
        // Get employees collection from Firebase
        const querySnapshot = await getDocs(collection(db, "employees"));
        const employeeData = querySnapshot.docs.map(doc => ({
          id: doc.id,
          ...doc.data()
        }));
        setEmployees(employeeData);
      } catch (error) {
        console.error("Error fetching employees:", error);
      }
    };

    fetchEmployees();
  }, []);

  // Initialize timesheet data when employees or weekDates change
  useEffect(() => {
    if (employees.length > 0 && weekDates.length > 0) {
      const initialTimesheetData = employees.reduce((acc, employee) => {
        acc[employee.id] = weekDates.reduce((weekAcc, date) => {
          weekAcc[date] = ""; // Initially no work hours
          return weekAcc;
        }, {});
        return acc;
      }, {});
      setTimesheetData(initialTimesheetData);
      fetchTimesheetData(); // Fetch timesheet data when employees or weekDates change
    }
  }, [employees, weekDates]);

  // Handle input changes for work hours
  const handleInputChange = async (employeeId, date, hours) => {
    setTimesheetData((prevData) => {
      const newData = { ...prevData };
      newData[employeeId][date] = hours;
      return newData;
    });

    // Save updated timesheet data to Firebase
    await saveTimesheetDataToFirebase(employeeId, date, hours);
  };

  // Handle adding a new employee manually and save to Firebase
  const handleAddEmployee = async () => {
    if (newEmployeeName.trim() === "") return; // Don't add empty names

    try {
      // Add new employee to Firebase collection
      const docRef = await addDoc(collection(db, "employees"), {
        name: newEmployeeName,
      });
      // Add new employee locally to the state
      const newEmployee = {
        id: docRef.id, // Use the Firebase document ID as the employee's ID
        name: newEmployeeName,
      };
      setEmployees((prevEmployees) => [...prevEmployees, newEmployee]);
      setNewEmployeeName(""); // Clear the input after adding
    } catch (error) {
      console.error("Error adding employee to Firebase:", error);
    }
  };

  // Handle employee name change
  const handleEmployeeNameChange = async (employeeId, newName) => {
    try {
      // Update employee name in Firestore
      const employeeDoc = doc(db, "employees", employeeId);
      await updateDoc(employeeDoc, { name: newName });

      // Update local employee list
      setEmployees((prevEmployees) =>
        prevEmployees.map((employee) =>
          employee.id === employeeId ? { ...employee, name: newName } : employee
        )
      );
    } catch (error) {
      console.error("Error updating employee name:", error);
    }
  };

  // Function to save timesheet data to Firebase
  const saveTimesheetDataToFirebase = async (employeeId, date, hours) => {
    try {
      const timesheetDocRef = doc(db, "timesheets", employeeId); // Use employee ID as doc reference
      const timesheetData = await getDoc(timesheetDocRef); // Check if document already exists

      let updatedData = {}; // Initialize an object to hold updated timesheet data

      if (timesheetData.exists()) {
        // If the document exists, get the current data and merge the new data
        updatedData = timesheetData.data().hours;
        updatedData[date] = hours; // Update the specific date with the new hours
      } else {
        // If the document does not exist, create new data
        updatedData = {
          [date]: hours, // Set initial value for this date
        };
      }

      // Save or update the document in Firestore with the merged data
      await setDoc(timesheetDocRef, { hours: updatedData });

      // After saving the data, fetch the updated timesheet data
      fetchTimesheetData(); // Refetch the data after saving
    } catch (error) {
      console.error("Error saving timesheet data to Firebase:", error);
    }
  };

  // Function to fetch the latest timesheet data
  const fetchTimesheetData = async () => {
    try {
      const querySnapshot = await getDocs(collection(db, "timesheets"));
      const updatedTimesheetData = {};

      querySnapshot.forEach((doc) => {
        const data = doc.data();
        updatedTimesheetData[doc.id] = data.hours;
      });

      setTimesheetData(updatedTimesheetData); // Update state with the fetched data
    } catch (error) {
      console.error("Error fetching timesheet data:", error);
    }
  };

  return (
    <div>
      <div>
        <input
          type="text"
          value={newEmployeeName}
          onChange={(e) => setNewEmployeeName(e.target.value)}
          placeholder="Enter new employee name"
        />
        <button onClick={handleAddEmployee}>Add Employee</button>
      </div>

      <table border="1">
        <thead>
          <tr>
            <th>Employee</th>
            {weekDates.map((date) => (
              <th key={date}>{date}</th>
            ))}
          </tr>
        </thead>
        <tbody>
          {employees.map((employee) => (
            <tr key={employee.id}>
              <td>
                <input
                  type="text"
                  value={employee.name}
                  onChange={(e) =>
                    handleEmployeeNameChange(employee.id, e.target.value)
                  }
                />
              </td>
              {weekDates.map((date) => (
                <td key={date}>
                  <input
                    type="number"
                    value={timesheetData[employee.id]?.[date] || 0} // Display 0 if no data
                    onChange={(e) =>
                      handleInputChange(
                        employee.id,
                        date,
                        e.target.value
                      )
                    }
                  />
                </td>
              ))}
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default TimesheetList;
