import React, { useEffect } from "react";
import "./login.css";

const Login = ({ onLogin }) => {
  useEffect(() => {
    const generateParticles = () => {
      const container = document.querySelector('.neural-background');
      const numParticles = 15;

      for (let i = 0; i < numParticles; i++) {
        // Create particle
        const particle = document.createElement('div');
        particle.classList.add('particle');
        const size = Math.random() * 5 + 3; // Particle size
        const xPos = Math.random() * window.innerWidth;
        const yPos = Math.random() * window.innerHeight;

        particle.style.width = `${size}px`;
        particle.style.height = `${size}px`;
        particle.style.left = `${xPos}px`;
        particle.style.top = `${yPos}px`;

        // Create random line for each particle
        const line = document.createElement('div');
        line.classList.add('line');
        const rotation = Math.random() * 360; // Random rotation angle
        line.style.transform = `rotate(${rotation}deg)`;
        line.style.left = `${xPos + size / 2}px`;
        line.style.top = `${yPos + size / 2}px`;
        line.style.height = `${Math.random() * 50 + 20}px`; // Random line length

        container.appendChild(particle);
        container.appendChild(line);
      }
    };

    generateParticles();
  }, []);

  const [username, setUsername] = React.useState("");
  const [password, setPassword] = React.useState("");

  const handleSubmit = (e) => {
    e.preventDefault();
    onLogin(username, password);
  };

  return (
    <div className="login-container">
      <div className="neural-background"></div>
      <div className="login-box">
        <h2 className="login-title">Login</h2>
        <form onSubmit={handleSubmit}>
          <div className="input-group">
            <label htmlFor="username">Username</label>
            <input
              type="text"
              id="username"
              value={username}
              onChange={(e) => setUsername(e.target.value)}
              placeholder="Enter your username"
              required
            />
          </div>
          <div className="input-group">
            <label htmlFor="password">Password</label>
            <input
              type="password"
              id="password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              placeholder="Enter your password"
              required
            />
          </div>
          <button type="submit" className="login-btn">Login</button>
        </form>
      </div>
    </div>
  );
};

export default Login;
