import React, { useState, useEffect } from 'react';

const TimeSheetApp = () => {
  // State variables
  const [users, setUsers] = useState([]);
  const [startTime, setStartTime] = useState('');
  const [endTime, setEndTime] = useState('');
  const [nextDay, setNextDay] = useState(false);
  const [totalTime, setTotalTime] = useState(0);
  const [nightShiftHours, setNightShiftHours] = useState(0);
  const [employeeName, setEmployeeName] = useState('');
  const [timesheets, setTimesheets] = useState([]);

  // Fetch users from backend (dummy data in this case)
  useEffect(() => {
    // Simulate fetching users from backend
    const fetchedUsers = [
      { id: 1, name: 'Alice', timesheets: [] },
      { id: 2, name: 'Bob', timesheets: [] }
    ];
    setUsers(fetchedUsers);
  }, []);

  // Calculate total time and night shift
  const calculateTimes = () => {
    if (!startTime || !endTime) return;

    const startParts = startTime.split(':').map(Number);
    const endParts = endTime.split(':').map(Number);

    let startDate = new Date();
    startDate.setHours(startParts[0], startParts[1], 0);

    let endDate = new Date();
    endDate.setHours(endParts[0], endParts[1], 0);

    // Handle next day case
    if (nextDay) {
      endDate.setDate(endDate.getDate() + 1); // Shift goes to next day
    }

    const totalTimeInHours = (endDate - startDate) / (1000 * 60 * 60); // Convert to hours

    // Calculate night shift hours (between 10 PM and 6 AM)
    const calculateNightShift = (start, end) => {
      let nightShift = 0;
      const nightShiftStart = 22; // 10 PM
      const nightShiftEnd = 6; // 6 AM

      if (start.getHours() < nightShiftStart && end.getHours() >= nightShiftStart) {
        nightShift += Math.min(end.getHours(), 24) - nightShiftStart;
      }

      if (start.getHours() >= nightShiftStart) {
        nightShift += Math.min(end.getHours(), nightShiftStart + 12) - start.getHours();
      }

      return nightShift;
    };

    const nightShift = calculateNightShift(startDate, endDate);

    setTotalTime(totalTimeInHours.toFixed(2));
    setNightShiftHours(nightShift.toFixed(2));
  };

  // Check if end time is before start time
  const isEndBeforeStart = () => {
    const startParts = startTime.split(':').map(Number);
    const endParts = endTime.split(':').map(Number);

    const startDate = new Date();
    startDate.setHours(startParts[0], startParts[1], 0);

    const endDate = new Date();
    endDate.setHours(endParts[0], endParts[1], 0);

    return endDate < startDate;
  };

  // Save timesheet to specific user
  const saveTimesheet = () => {
    if (isEndBeforeStart()) {
      alert("End time cannot be before start time.");
      return;
    }

    const newTimesheet = {
      employeeName,
      startTime,
      endTime,
      totalTime,
      nightShiftHours,
      nextDay
    };

    // Update the user's timesheet list
    const updatedUsers = users.map(user => {
      if (user.name === employeeName) {
        return {
          ...user,
          timesheets: [...user.timesheets, newTimesheet]
        };
      }
      return user;
    });

    setUsers(updatedUsers);

    // Reset input fields
    setStartTime('');
    setEndTime('');
    setNextDay(false);
    setTotalTime(0);
    setNightShiftHours(0);
    setEmployeeName('');
  };

  return (
    <div>
      <h1>Employee Timesheet</h1>

      {/* Select Employee Name */}
      <div>
        <label>Employee Name</label>
        <select onChange={(e) => setEmployeeName(e.target.value)} value={employeeName}>
          <option value="">Select Employee</option>
          {users.map((user) => (
            <option key={user.id} value={user.name}>
              {user.name}
            </option>
          ))}
        </select>
      </div>

      {/* Time Inputs */}
      <div>
        <label>Start Time</label>
        <input
          type="time"
          value={startTime}
          onChange={(e) => setStartTime(e.target.value)}
        />
      </div>

      <div>
        <label>End Time</label>
        <input
          type="time"
          value={endTime}
          onChange={(e) => setEndTime(e.target.value)}
        />
      </div>

      <div>
        <label>Next Day</label>
        <input
          type="checkbox"
          checked={nextDay}
          onChange={() => setNextDay(!nextDay)}
        />
      </div>

      {/* Calculate and Show Results */}
      <div>
        <button onClick={calculateTimes}>Calculate</button>
      </div>

      <div>
        <p>Total Time: {totalTime} hours</p>
        <p>Night Shift: {nightShiftHours} hours</p>
      </div>

      {/* Save Timesheet */}
      <div>
        <button onClick={saveTimesheet}>Save Timesheet</button>
      </div>

      <h2>Timesheet List</h2>
      {users.map((user) => (
        <div key={user.id}>
          <h3>{user.name}</h3>
          <table>
            <thead>
              <tr>
                <th>Start Time</th>
                <th>End Time</th>
                <th>Total Time</th>
                <th>Night Shift</th>
              </tr>
            </thead>
            <tbody>
              {user.timesheets.map((timesheet, index) => (
                <tr key={index}>
                  <td>{timesheet.startTime}</td>
                  <td>{timesheet.endTime}</td>
                  <td>{timesheet.totalTime}</td>
                  <td>{timesheet.nightShiftHours}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      ))}
    </div>
  );
};

export default TimeSheetApp;
