import React, { useState, useEffect } from "react";
import "./login.css"; // Import custom CSS for Login

const Login = ({ onLogin }) => {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");

  // Track if inputs are filled to change cursor
  const [cursorClass, setCursorClass] = useState("");

  // Update cursor when either field is filled
  useEffect(() => {
    if (username || password) {
      setCursorClass("input-filled");  // Add class if either field is filled
    } else {
      setCursorClass("");  // Reset class if both fields are empty
    }
  }, [username, password]);  // Depend on username and password

  const handleSubmit = (e) => {
    e.preventDefault();
    onLogin(username, password);
  };

  return (
    <div className={`login-container ${cursorClass}`}>
      <div className="login-box">
        <h2 className="login-title">Login</h2>
        <form onSubmit={handleSubmit}>
          <div className="input-group">
            <label htmlFor="username">Username</label>
            <input
              type="text"
              id="username"
              value={username}
              onChange={(e) => setUsername(e.target.value)}
              placeholder="Enter your username"
              required
            />
          </div>
          <div className="input-group">
            <label htmlFor="password">Password</label>
            <input
              type="password"
              id="password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              placeholder="Enter your password"
              required
            />
          </div>
          <button type="submit" className="login-btn">Login</button>
        </form>
      </div>
    </div>
  );
};

export default Login;
