// src/TimesheetList.js
import React, { useEffect, useState } from 'react';
import { collection, getDocs } from 'firebase/firestore';
import { db } from './firebaseConfig';
import { Card, CardContent, Typography, Grid, Box } from '@mui/material';

function TimesheetList() {
  const [timesheets, setTimesheets] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      const querySnapshot = await getDocs(collection(db, 'timesheets'));
      const timesheetData = querySnapshot.docs.map(doc => ({
        id: doc.id,
        ...doc.data()
      }));
      setTimesheets(timesheetData);
    };

    fetchData();
  }, []);

  return (
    <Box sx={{ padding: 4 }}>
      <Typography variant="h4" gutterBottom>Employee Timesheets</Typography>
      <Grid container spacing={3}>
        {timesheets.map((entry) => (
          <Grid item xs={12} sm={6} md={4} key={entry.id}>
            <Card variant="outlined" sx={{ borderRadius: 2, boxShadow: 3 }}>
              <CardContent>
                <Typography variant="h6" color="primary" gutterBottom>
                  {entry.name}
                </Typography>
                <Typography variant="body1">
                  <strong>Hours Worked:</strong> {entry.hoursWorked} hours
                </Typography>
                <Typography variant="body2" color="text.secondary">
                  <strong>From:</strong> {entry.startTime.toDate().toLocaleString()}
                </Typography>
                <Typography variant="body2" color="text.secondary">
                  <strong>To:</strong> {entry.endTime.toDate().toLocaleString()}
                </Typography>
              </CardContent>
            </Card>
          </Grid>
        ))}
      </Grid>
    </Box>
  );
}

export default TimesheetList;
