import React, { useEffect, useRef, useState } from "react";
import "./login.css";

const Login = ({ onLogin }) => {
  const canvasRef = useRef(null);
  const [particles, setParticles] = useState([]);
  const numParticles = 30;

  useEffect(() => {
    const canvas = canvasRef.current;
    const context = canvas.getContext("2d");
    canvas.width = window.innerWidth;
    canvas.height = window.innerHeight;

    // Initialize particles with random positions
    const particlesArray = [];
    for (let i = 0; i < numParticles; i++) {
      particlesArray.push({
        x: Math.random() * canvas.width,
        y: Math.random() * canvas.height,
        size: 3,
        originalX: Math.random() * canvas.width,
        originalY: Math.random() * canvas.height,
        velocityX: 0,
        velocityY: 0,
      });
    }
    setParticles(particlesArray);

    let mouse = { x: null, y: null };

    const drawParticles = () => {
      context.clearRect(0, 0, canvas.width, canvas.height);

      // Draw particles and lines
      particles.forEach((particle, index) => {
        context.beginPath();
        context.arc(particle.x, particle.y, particle.size, 0, Math.PI * 2);
        context.fillStyle = "#ffd700";
        context.fill();

        // Connect nearby particles with lines for a web effect
        for (let j = index + 1; j < particles.length; j++) {
          const otherParticle = particles[j];
          const dx = particle.x - otherParticle.x;
          const dy = particle.y - otherParticle.y;
          const distance = Math.sqrt(dx * dx + dy * dy);

          if (distance < 100) {
            context.beginPath();
            context.moveTo(particle.x, particle.y);
            context.lineTo(otherParticle.x, otherParticle.y);
            context.strokeStyle = `rgba(255, 215, 0, ${1 - distance / 100})`;
            context.lineWidth = 0.5;
            context.stroke();
          }
        }
      });
    };

    const updateParticles = () => {
      particles.forEach((particle) => {
        const dx = mouse.x - particle.x;
        const dy = mouse.y - particle.y;
        const dist = Math.sqrt(dx * dx + dy * dy);

        // Apply effect when cursor is near particles
        const maxDist = 150;
        if (dist < maxDist) {
          const pullStrength = (maxDist - dist) / maxDist;
          particle.velocityX += dx * pullStrength * 0.03;
          particle.velocityY += dy * pullStrength * 0.03;
        }

        // Apply a drag force to slowly return to original position
        const springForce = 0.05;
        particle.velocityX += (particle.originalX - particle.x) * springForce;
        particle.velocityY += (particle.originalY - particle.y) * springForce;

        // Update particle position and velocity
        particle.x += particle.velocityX;
        particle.y += particle.velocityY;

        // Add friction to slow down particle movement over time
        particle.velocityX *= 0.92;
        particle.velocityY *= 0.92;
      });
      drawParticles();
      requestAnimationFrame(updateParticles);
    };

    updateParticles();

    const handleMouseMove = (event) => {
      mouse.x = event.clientX;
      mouse.y = event.clientY;
    };

    window.addEventListener("mousemove", handleMouseMove);
    window.addEventListener("resize", () => {
      canvas.width = window.innerWidth;
      canvas.height = window.innerHeight;
      drawParticles();
    });

    return () => {
      window.removeEventListener("mousemove", handleMouseMove);
    };
  }, []);

  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");

  const handleSubmit = (e) => {
    e.preventDefault();
    onLogin(username, password);
  };

  return (
    <div className="login-container">
      <canvas ref={canvasRef} className="mongolia-background"></canvas>
      <div className="login-box">
        <h2 className="login-title">Login</h2>
        <form onSubmit={handleSubmit}>
          <div className="input-group">
            <label htmlFor="username">Username</label>
            <input
              type="text"
              id="username"
              value={username}
              onChange={(e) => setUsername(e.target.value)}
              placeholder="Enter your username"
              required
            />
          </div>
          <div className="input-group">
            <label htmlFor="password">Password</label>
            <input
              type="password"
              id="password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              placeholder="Enter your password"
              required
            />
          </div>
          <button type="submit" className="login-btn">Login</button>
        </form>
      </div>
    </div>
  );
};

export default Login;
