import React, { useState, useEffect, useRef } from "react";
import "./login.css"; // Import custom CSS for Login

const Login = ({ onLogin }) => {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [cursorClass, setCursorClass] = useState("");

  // Reference for the shadow circle
  const shadowCircleRef = useRef(null);

  // Track mouse position to move shadow circle
  const [cursorPosition, setCursorPosition] = useState({ x: 0, y: 0 });

  // Update cursor when either field is filled or focused
  useEffect(() => {
    if (username || password) {
      setCursorClass("input-filled");  // Add class if either field is filled
    } else {
      setCursorClass("");  // Reset class if both fields are empty
    }
  }, [username, password]);

  // Handle mouse movement and update shadow circle position
  useEffect(() => {
    const handleMouseMove = (e) => {
      setCursorPosition({ x: e.clientX, y: e.clientY });

      if (shadowCircleRef.current) {
        // Apply smaller delay effect with smooth transition to follow the cursor
        shadowCircleRef.current.style.transform = `translate(${e.clientX + 1}px, ${e.clientY + 1}px)`;  // Reduced offset for closer shadow
      }
    };

    // Add event listener for mouse move
    window.addEventListener("mousemove", handleMouseMove);

    // Clean up event listener
    return () => {
      window.removeEventListener("mousemove", handleMouseMove);
    };
  }, []);

  // Handle form submission
  const handleSubmit = (e) => {
    e.preventDefault();
    onLogin(username, password);
  };

  return (
    <div className={`login-container ${cursorClass}`}>
      {/* Transparent circle element that follows the cursor */}
      <div ref={shadowCircleRef} className="shadow-circle"></div>

      <div className="login-box">
        <h2 className="login-title">Login</h2>
        <form onSubmit={handleSubmit}>
          <div className="input-group">
            <label htmlFor="username">Username</label>
            <input
              type="text"
              id="username"
              value={username}
              onChange={(e) => setUsername(e.target.value)}
              placeholder="Enter your username"
              required
              onFocus={() => setCursorClass("input-filled")}
            />
          </div>
          <div className="input-group">
            <label htmlFor="password">Password</label>
            <input
              type="password"
              id="password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              placeholder="Enter your password"
              required
              onFocus={() => setCursorClass("input-filled")}
            />
          </div>
          <button type="submit" className="login-btn">Login</button>
        </form>
      </div>
    </div>
  );
};

export default Login;
