import React, { useState, useEffect, useRef, useCallback } from "react";
import { getStartOfWeek, getWeekDates } from "./utils";
import TimesheetList from "./TimesheetList";
import EmployeeManagement from "./EmployeeManagement"; // Import EmployeeManagement component
import "./App.css"; // Import custom CSS for styling (optional)

const App = () => {
  const [currentWeek, setCurrentWeek] = useState(getStartOfWeek(new Date()));
  const [weekDates, setWeekDates] = useState(getWeekDates(currentWeek));
  const [view, setView] = useState("timesheet"); // State to manage which view to show: "timesheet", or "employeeManagement"
  const [isLoggedIn, setIsLoggedIn] = useState(false); // Login state
  const [username, setUsername] = useState(""); // Username state
  const [password, setPassword] = useState(""); // Password state
  const [loginError, setLoginError] = useState(""); // Error message for failed login
  const [cursorClass, setCursorClass] = useState(""); // For custom cursor behavior

  // Function to handle login
  const handleLogin = () => {
    if (username === "admin" && password === "password") {
      setIsLoggedIn(true); // Simulating a successful login
      setLoginError(""); // Clear any previous errors
    } else {
      setLoginError("Invalid username or password."); // Show error on failed login
    }
  };

  // Function to handle changing weeks
  const changeWeek = useCallback((direction) => {
    const newWeek = new Date(currentWeek);
    newWeek.setDate(currentWeek.getDate() + direction * 7);
    setCurrentWeek(newWeek);
    setWeekDates(getWeekDates(newWeek));
  }, [currentWeek]);

  // Function to switch between views
  const handleViewChange = (view) => {
    setView(view);
  };

  // Handle mouse movement and update shadow circle position with delay
  const shadowCircleRef = useRef(null);
  const [cursorPosition, setCursorPosition] = useState({ x: 0, y: 0 });
  const [shadowPosition, setShadowPosition] = useState({ x: 0, y: 0 });

  useEffect(() => {
    const delay = 100;

    const handleMouseMove = (e) => {
      setCursorPosition({ x: e.clientX, y: e.clientY });

      setTimeout(() => {
        setShadowPosition({ x: e.clientX + 10, y: e.clientY + 10 });
      }, delay);
    };

    window.addEventListener("mousemove", handleMouseMove);

    return () => {
      window.removeEventListener("mousemove", handleMouseMove);
    };
  }, []);

  // Update cursor class based on input values
  useEffect(() => {
    if (username || password) {
      setCursorClass("input-filled");
    } else {
      setCursorClass("");
    }
  }, [username, password]);

  // Login Form Component
  const renderLoginForm = () => (
    <div className={`login-container ${cursorClass}`}>
      <div
        ref={shadowCircleRef}
        className="shadow-circle"
        style={{
          left: `${shadowPosition.x}px`,
          top: `${shadowPosition.y}px`,
        }}
      ></div>

      <div className="login-box">
        <h2 className="login-title">Login</h2>
        <form onSubmit={(e) => e.preventDefault()}>
          <div className="input-group">
            <input
              type="text"
              value={username}
              onChange={(e) => setUsername(e.target.value)}
              placeholder="Username"
              required
            />
          </div>
          <div className="input-group">
            <input
              type="password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              placeholder="Password"
              required
            />
          </div>
          {loginError && <div className="error-message">{loginError}</div>}
          <button onClick={handleLogin} className="login-btn">Login</button>
        </form>
      </div>
    </div>
  );

  return (
    <div className="app-container">
      {/* If not logged in, show login form */}
      {!isLoggedIn ? (
        renderLoginForm()
      ) : (
        <>
          {/* Top Navigation Bar with Split Layout */}
          <div className="navbar">
            <div
              className={`nav-item ${view === "timesheet" ? "selected" : ""}`}
              onClick={() => handleViewChange("timesheet")}
            >
              Timesheet
            </div>
            <div
              className={`nav-item ${view === "employeeManagement" ? "selected" : ""}`}
              onClick={() => handleViewChange("employeeManagement")}
            >
              Employees
            </div>
          </div>

          {/* Main content area */}
          <div className="main-content">
            {/* Conditional rendering based on the selected view */}
            {view === "timesheet" && <TimesheetList weekDates={weekDates} />}
            {view === "employeeManagement" && <EmployeeManagement />}
          </div>
        </>
      )}
    </div>
  );
};

export default App;
