import { useState, useEffect } from "react";
import { collection, getDocs, getDoc, doc, setDoc } from "firebase/firestore"; 
import { db } from "./firebaseConfig";

const TimesheetList = ({ weekDates }) => {
  const [employees, setEmployees] = useState([]);
  const [timesheetData, setTimesheetData] = useState({});
  const [timeAdjustments, setTimeAdjustments] = useState({});
  const [modalOpen, setModalOpen] = useState(false);
  const [selectedEmployee, setSelectedEmployee] = useState(null);
  const [selectedDate, setSelectedDate] = useState(null);
  const [startTime, setStartTime] = useState("");
  const [endTime, setEndTime] = useState("");
  const [totalTime, setTotalTime] = useState(0);
  const [nightShiftHours, setNightShiftHours] = useState(0); // Track night shift hours
  const [nextDay, setNextDay] = useState(false); // Add "Next Day" flag

  useEffect(() => {
    const fetchData = async () => {
      try {
        const employeeSnapshot = await getDocs(collection(db, "employees"));
        const employeeData = employeeSnapshot.docs.map(doc => ({
          id: doc.id,
          ...doc.data()
        }));
        setEmployees(employeeData);

        const settingsDocRef = doc(db, "timeAdjustments", "settings");
        const settingsDoc = await getDoc(settingsDocRef);
        if (settingsDoc.exists()) {
          setTimeAdjustments(settingsDoc.data());
        } else {
          console.log("No time adjustments found");
        }
      } catch (error) {
        console.error("Error fetching data:", error);
      }
    };

    fetchData();
  }, []);

  useEffect(() => {
    if (employees.length > 0 && weekDates.length > 0) {
      const initialTimesheetData = employees.reduce((acc, employee) => {
        acc[employee.id] = weekDates.reduce((weekAcc, date) => {
          weekAcc[date] = { startTime: "", endTime: "", totalTime: 0, nightShift: 0 };
          return weekAcc;
        }, {});
        return acc;
      }, {});
      setTimesheetData(initialTimesheetData);
      fetchTimesheetData();
    }
  }, [employees, weekDates]);

  // Function to calculate total time and night shift hours
  const calculateTotalTimeAndNightShift = (start, end, nextDay) => {
    const startParts = start.split(":").map(Number); // [hours, minutes]
    const endParts = end.split(":").map(Number);

    // Convert start and end times into Date objects
    const startDate = new Date();
    startDate.setHours(startParts[0], startParts[1], 0);

    const endDate = new Date();
    endDate.setHours(endParts[0], endParts[1], 0);

    // Adjust for next day if selected
    if (nextDay) {
      endDate.setDate(endDate.getDate() + 1); // Adjust the end date to the next day
    }

    // Calculate total time worked (in hours)
    const totalTimeInHours = (endDate - startDate) / (1000 * 60 * 60); // Convert to hours

    // Night shift hours (10:00 PM to 6:00 AM)
    const nightShiftStart = 22; // 10:00 PM
    const nightShiftEnd = 6; // 6:00 AM

    let nightShiftHours = 0;

    // Night shift overlap calculation
    const calculateNightShiftOverlap = (startDate, endDate) => {
      let nightShift = 0;

      const startHour = startDate.getHours();
      const startMinutes = startDate.getMinutes();
      const endHour = endDate.getHours();
      const endMinutes = endDate.getMinutes();

      if (startHour < nightShiftStart) {
        if (endHour >= nightShiftStart) {
          const overlapStart = nightShiftStart;
          const overlapEnd = endHour + (endMinutes / 60);
          nightShift += overlapEnd - overlapStart;
        }
      } else if (startHour >= nightShiftStart && startHour < 24) {
        const overlapStart = startHour + (startMinutes / 60);
        const overlapEnd = endHour + (endMinutes / 60);
        nightShift += Math.max(0, overlapEnd - overlapStart);
      }

      if (startHour >= 0 && startHour < nightShiftEnd) {
        const overlapStart = Math.max(startHour, 0) + startMinutes / 60;
        const overlapEnd = Math.min(endHour, nightShiftEnd) + endMinutes / 60;
        nightShift += overlapEnd - overlapStart;
      }

      return nightShift;
    };

    nightShiftHours = calculateNightShiftOverlap(startDate, endDate);

    return { 
      totalTime: totalTimeInHours.toFixed(2), 
      nightShift: nightShiftHours.toFixed(2) 
    };
  };

  const handleInputChange = async (employeeId, date, start, end, totalTime, nightShift) => {
    setTimesheetData((prevData) => {
      const newData = { ...prevData };
      newData[employeeId][date] = { startTime: start, endTime: end, totalTime, nightShift };
      return newData;
    });

    await saveTimesheetDataToFirebase(employeeId, date, start, end, totalTime, nightShift);
  };

  const saveTimesheetDataToFirebase = async (employeeId, date, start, end, totalTime, nightShift) => {
    try {
      const timesheetDocRef = doc(db, "timesheets", employeeId);
      const timesheetData = await getDoc(timesheetDocRef);

      let updatedData = {};

      if (timesheetData.exists()) {
        updatedData = timesheetData.data().hours;
        updatedData[date] = { startTime: start, endTime: end, totalTime, nightShift };
      } else {
        updatedData = { [date]: { startTime: start, endTime: end, totalTime, nightShift } };
      }

      await setDoc(timesheetDocRef, { hours: updatedData });
      fetchTimesheetData();
    } catch (error) {
      console.error("Error saving timesheet data:", error);
    }
  };

  const fetchTimesheetData = async () => {
    try {
      const querySnapshot = await getDocs(collection(db, "timesheets"));
      const updatedTimesheetData = {};

      querySnapshot.forEach((doc) => {
        const data = doc.data();
        updatedTimesheetData[doc.id] = data.hours;
      });

      setTimesheetData(updatedTimesheetData);
    } catch (error) {
      console.error("Error fetching timesheet data:", error);
    }
  };

  const handleSetTime = (employeeId, date) => {
    setSelectedEmployee(employeeId);
    setSelectedDate(date);
    setModalOpen(true);
  };

  const handleCloseModal = () => {
    setModalOpen(false);
    setStartTime("");
    setEndTime("");
    setTotalTime(0);
    setNightShiftHours(0); // Reset night shift
    setNextDay(false); // Reset Next Day flag
  };

  const handleStartTimeChange = (e) => {
    setStartTime(e.target.value);
    if (endTime) {
      const { totalTime, nightShift } = calculateTotalTimeAndNightShift(e.target.value, endTime, nextDay);
      setTotalTime(totalTime);
      setNightShiftHours(nightShift);
    }
  };

  const handleEndTimeChange = (e) => {
    setEndTime(e.target.value);
    if (startTime) {
      const { totalTime, nightShift } = calculateTotalTimeAndNightShift(startTime, e.target.value, nextDay);
      setTotalTime(totalTime);
      setNightShiftHours(nightShift);
    }
  };

  return (
    <div>
      <div>
        <h3>Time Adjustments</h3>
        <p>Normal Start Time: {timeAdjustments.normalStartTime}</p>
        <p>Normal End Time: {timeAdjustments.normalEndTime}</p>
      </div>

      <table border="1">
        <thead>
          <tr>
            <th>Employee</th>
            {weekDates.map((date) => (
              <th key={date}>{date}</th>
            ))}
          </tr>
        </thead>
        <tbody>
          {employees.map((employee) => (
            <tr key={employee.id}>
              <td>{employee.name}</td>
              {weekDates.map((date) => (
                <td key={date}>
                  <button onClick={() => handleSetTime(employee.id, date)}>
                    Set Time
                  </button>
                </td>
              ))}
            </tr>
          ))}
        </tbody>
      </table>

      {modalOpen && (
        <div className="modal">
          <h2>Set Time for {selectedEmployee}</h2>
          <div>
            <label>Start Time</label>
            <input type="time" value={startTime} onChange={handleStartTimeChange} />
          </div>
          <div>
            <label>End Time</label>
            <input type="time" value={endTime} onChange={handleEndTimeChange} />
          </div>
          <div>
            <label>Next Day?</label>
            <input
              type="checkbox"
              checked={nextDay}
              onChange={() => setNextDay(!nextDay)}
            />
          </div>
          <div>
            <p>Total Time: {totalTime} hours</p>
            <p>Night Shift: {nightShiftHours} hours</p>
          </div>
          <div>
            <button onClick={() => handleInputChange(selectedEmployee, selectedDate, startTime, endTime, totalTime, nightShiftHours)}>
              Save Time
            </button>
            <button onClick={handleCloseModal}>Close</button>
          </div>
        </div>
      )}
    </div>
  );
};

export default TimesheetList;
