import React, { useState, useEffect } from "react";

const TimesheetList = ({ weekDates }) => {
  const [employees, setEmployees] = useState([]);
  const [timesheetData, setTimesheetData] = useState({});
  const [newEmployeeName, setNewEmployeeName] = useState(""); // State for new employee name input

  // Fetch employees data from the database
  useEffect(() => {
    const fetchEmployees = async () => {
      try {
        // Simulate fetching employee data from a database or API
        const response = await fetch("/api/employees"); // Replace with your API endpoint
        const data = await response.json();
        setEmployees(data);
      } catch (error) {
        console.error("Error fetching employees:", error);
      }
    };

    fetchEmployees();
  }, []);

  // Initialize timesheet data when employees or weekDates change
  useEffect(() => {
    if (employees.length > 0 && weekDates.length > 0) {
      const initialTimesheetData = employees.reduce((acc, employee) => {
        acc[employee.id] = weekDates.reduce((weekAcc, date) => {
          weekAcc[date] = ""; // Initially no work hours
          return weekAcc;
        }, {});
        return acc;
      }, {});
      setTimesheetData(initialTimesheetData);
    }
  }, [employees, weekDates]);

  // Handle input changes for work hours
  const handleInputChange = (employeeId, date, hours) => {
    setTimesheetData((prevData) => {
      const newData = { ...prevData };
      newData[employeeId][date] = hours;
      return newData;
    });
  };

  // Handle employee name change
  const handleNameChange = (employeeId, name) => {
    setEmployees((prevEmployees) =>
      prevEmployees.map((employee) =>
        employee.id === employeeId ? { ...employee, name } : employee
      )
    );
  };

  // Handle adding a new employee manually
  const handleAddEmployee = () => {
    if (newEmployeeName.trim() === "") return; // Don't add empty names
    const newEmployee = {
      id: employees.length + 1, // Create a new ID based on the current length (you may need a better ID generation method)
      name: newEmployeeName,
    };
    setEmployees((prevEmployees) => [...prevEmployees, newEmployee]);
    setNewEmployeeName(""); // Clear the input after adding
  };

  return (
    <div>
      <div>
        <input
          type="text"
          value={newEmployeeName}
          onChange={(e) => setNewEmployeeName(e.target.value)}
          placeholder="Enter new employee name"
        />
        <button onClick={handleAddEmployee}>Add Employee</button>
      </div>

      <table border="1">
        <thead>
          <tr>
            <th>Employee</th>
            {weekDates.map((date) => (
              <th key={date}>{date}</th>
            ))}
          </tr>
        </thead>
        <tbody>
          {employees.map((employee) => (
            <tr key={employee.id}>
              <td>
                <input
                  type="text"
                  value={employee.name}
                  onChange={(e) => handleNameChange(employee.id, e.target.value)}
                />
              </td>
              {weekDates.map((date) => (
                <td key={date}>
                  <input
                    type="number"
                    value={timesheetData[employee.id]?.[date] || ""}
                    onChange={(e) =>
                      handleInputChange(
                        employee.id,
                        date,
                        e.target.value
                      )
                    }
                  />
                </td>
              ))}
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default TimesheetList;
