// src/TimesheetList.js
import React, { useEffect, useState } from 'react';
import { collection, getDocs } from 'firebase/firestore';
import { db } from './firebaseConfig';
import { Table, TableBody, TableCell, TableContainer, TableHead, TableRow, Paper, TextField, Typography } from '@mui/material';

function TimesheetList() {
  const [timesheets, setTimesheets] = useState([]);
  const [dates, setDates] = useState([]);
  const [workHours, setWorkHours] = useState({}); // For tracking work hours entered manually

  useEffect(() => {
    const fetchData = async () => {
      const querySnapshot = await getDocs(collection(db, 'timesheets'));
      const timesheetData = querySnapshot.docs.map(doc => ({
        id: doc.id,
        ...doc.data(),
        date: doc.data().startTime.toDate().toLocaleDateString() // Extract date for easier display
      }));

      // Organize data by employee names
      const groupedData = timesheetData.reduce((acc, entry) => {
        const { name, date, hoursWorked } = entry;
        if (!acc[name]) acc[name] = {};
        acc[name][date] = hoursWorked;
        return acc;
      }, {});

      // Set unique dates in sorted order for table columns
      const uniqueDates = [...new Set(timesheetData.map(entry => entry.date))].sort();

      setDates(uniqueDates);
      setTimesheets(groupedData);
    };

    fetchData();
  }, []);

  // Function to get the current week's start and end date
  const getWeekDates = () => {
    const today = new Date();
    const startOfWeek = today.getDate() - today.getDay(); // Get the start of the week (Sunday)
    const weekDates = [];

    for (let i = 0; i < 7; i++) {
      const day = new Date(today.setDate(startOfWeek + i));
      const formattedDate = day.toLocaleDateString();
      weekDates.push(formattedDate);
    }

    return weekDates;
  };

  // Handle manual input of work hours
  const handleWorkHoursChange = (employeeName, date, value) => {
    setWorkHours((prev) => ({
      ...prev,
      [employeeName]: {
        ...prev[employeeName],
        [date]: value
      }
    }));
  };

  // Get the current week's dates
  const weekDates = getWeekDates();

  return (
    <TableContainer component={Paper} sx={{ padding: 2, boxShadow: 3 }}>
      <Typography variant="h4" gutterBottom align="center">Employee Timesheet</Typography>
      <Table>
        <TableHead>
          <TableRow>
            <TableCell><strong>Employee Name</strong></TableCell>
            {weekDates.map((date, index) => (
              <TableCell key={index} align="center"><strong>{date}</strong></TableCell>
            ))}
          </TableRow>
        </TableHead>
        <TableBody>
          {Object.keys(timesheets).map((name) => (
            <TableRow key={name}>
              <TableCell component="th" scope="row"><strong>{name}</strong></TableCell>
              {weekDates.map((date) => (
                <TableCell key={date} align="center">
                  <TextField
                    type="number"
                    value={workHours[name]?.[date] || timesheets[name]?.[date] || ''}
                    onChange={(e) => handleWorkHoursChange(name, date, e.target.value)}
                    size="small"
                    variant="outlined"
                    fullWidth
                    inputProps={{ min: 0 }}
                    sx={{ width: '80px' }}
                  />
                </TableCell>
              ))}
            </TableRow>
          ))}
        </TableBody>
      </Table>
    </TableContainer>
  );
}

export default TimesheetList;
