import { useEffect, useState } from "react";
import { db } from "./firebaseConfig"; // Import your Firebase config file
import { doc, setDoc, getDoc, onSnapshot } from "firebase/firestore";

// Function to initialize and fetch the employee data
const fetchTimesheetData = (employeeId, setTimesheetData) => {
  const timesheetDocRef = doc(db, "timesheets", employeeId);

  // Real-time listener for Firestore updates
  onSnapshot(timesheetDocRef, (docSnapshot) => {
    if (docSnapshot.exists()) {
      setTimesheetData(docSnapshot.data().hours);
    } else {
      setTimesheetData({}); // Initialize with empty data if no document exists
    }
  });
};

// Function to save timesheet data to Firebase
const saveTimesheetDataToFirebase = async (employeeId, date, hours) => {
  try {
    const timesheetDocRef = doc(db, "timesheets", employeeId);
    const timesheetData = await getDoc(timesheetDocRef);

    let updatedData = {};

    if (timesheetData.exists()) {
      // Merge new data with existing data
      updatedData = timesheetData.data().hours;
      updatedData[date] = hours;
    } else {
      // Create new document if none exists
      updatedData = { [date]: hours };
    }

    // Save the updated data to Firestore
    await setDoc(timesheetDocRef, { hours: updatedData });

  } catch (error) {
    console.error("Error saving timesheet data to Firebase:", error);
  }
};

const TimesheetList = ({ weekDates }) => {
  const [employees, setEmployees] = useState([]);
  const [timesheetData, setTimesheetData] = useState({});

  // Fetch employee data (assuming it’s fetched from a different API or database)
  useEffect(() => {
    const fetchEmployees = async () => {
      try {
        const response = await fetch("/api/employees");
        const data = await response.json();
        setEmployees(data);
      } catch (error) {
        console.error("Error fetching employees:", error);
      }
    };

    fetchEmployees();
  }, []);

  // Set up real-time fetching for each employee's timesheet data
  useEffect(() => {
    if (employees.length > 0) {
      employees.forEach((employee) => {
        fetchTimesheetData(employee.id, (data) => {
          setTimesheetData((prevData) => ({
            ...prevData,
            [employee.id]: data,
          }));
        });
      });
    }
  }, [employees]);

  // Handle input changes for work hours
  const handleInputChange = (employeeId, date, hours) => {
    setTimesheetData((prevData) => {
      const newData = { ...prevData };
      if (!newData[employeeId]) {
        newData[employeeId] = {};
      }
      newData[employeeId][date] = hours;
      return newData;
    });

    // Save updated timesheet data to Firebase
    saveTimesheetDataToFirebase(employeeId, date, hours);
  };

  return (
    <div>
      <table border="1">
        <thead>
          <tr>
            <th>Employee</th>
            {weekDates.map((date) => (
              <th key={date}>{date}</th>
            ))}
          </tr>
        </thead>
        <tbody>
          {employees.map((employee) => (
            <tr key={employee.id}>
              <td>{employee.name}</td>
              {weekDates.map((date) => (
                <td key={date}>
                  <input
                    type="number"
                    value={timesheetData[employee.id]?.[date] || ""}
                    onChange={(e) =>
                      handleInputChange(employee.id, date, e.target.value)
                    }
                  />
                </td>
              ))}
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default TimesheetList;
