import { useState, useEffect } from "react";
import { collection, getDocs, getDoc, doc, setDoc } from "firebase/firestore"; 
import { db } from "./firebaseConfig";

const TimesheetList = ({ weekDates }) => {
  const [employees, setEmployees] = useState([]);
  const [timesheetData, setTimesheetData] = useState({});
  const [timeAdjustments, setTimeAdjustments] = useState({});
  const [modalOpen, setModalOpen] = useState(false);
  const [selectedEmployee, setSelectedEmployee] = useState(null);
  const [selectedDate, setSelectedDate] = useState(null);
  const [startTime, setStartTime] = useState("");
  const [endTime, setEndTime] = useState("");
  const [totalTime, setTotalTime] = useState(0);
  const [overtime, setOvertime] = useState(0); // State for overtime hours

  useEffect(() => {
    const fetchData = async () => {
      try {
        const employeeSnapshot = await getDocs(collection(db, "employees"));
        const employeeData = employeeSnapshot.docs.map(doc => ({
          id: doc.id,
          ...doc.data()
        }));
        setEmployees(employeeData);

        const settingsDocRef = doc(db, "timeAdjustments", "settings");
        const settingsDoc = await getDoc(settingsDocRef);
        if (settingsDoc.exists()) {
          setTimeAdjustments(settingsDoc.data());
        } else {
          console.log("No time adjustments found");
        }
      } catch (error) {
        console.error("Error fetching data:", error);
      }
    };

    fetchData();
  }, []);

  useEffect(() => {
    if (employees.length > 0 && weekDates.length > 0) {
      const initialTimesheetData = employees.reduce((acc, employee) => {
        acc[employee.id] = weekDates.reduce((weekAcc, date) => {
          weekAcc[date] = { startTime: "", endTime: "", totalTime: 0, overtime: 0 };
          return weekAcc;
        }, {});
        return acc;
      }, {});
      setTimesheetData(initialTimesheetData);
      fetchTimesheetData();
    }
  }, [employees, weekDates]);

  // Function to calculate total time and overtime
  const calculateTotalTime = (start, end) => {
    const startParts = start.split(":").map(Number);
    const endParts = end.split(":").map(Number);

    const startDate = new Date();
    startDate.setHours(startParts[0], startParts[1], 0);

    const endDate = new Date();
    endDate.setHours(endParts[0], endParts[1], 0);

    // If the end time is earlier than the start time, adjust for crossing midnight
    if (endDate < startDate) {
        endDate.setDate(endDate.getDate() + 1); // Adjust the end date to the next day
    }

    const totalTimeInHours = (endDate - startDate) / (1000 * 60 * 60); // Calculate total hours worked
    setTotalTime(totalTimeInHours.toFixed(2));

    const normalStartTime = 9; // Normal work start time (9:00 AM)
    const normalEndTime = 18; // Normal work end time (6:00 PM)
    let overtime = 0;
    let normalTime = 0;

    // Calculate normal time (between 9:00 AM and 6:00 PM)
    if (startDate.getHours() < normalStartTime) {
        const adjustedStartDate = new Date();
        adjustedStartDate.setHours(normalStartTime, 0, 0); // Start time is 9:00 AM
        normalTime += (endDate - adjustedStartDate) / (1000 * 60 * 60); // Time between 9:00 AM and end time
    } else if (startDate.getHours() >= normalStartTime && startDate.getHours() < normalEndTime) {
        const adjustedEndDate = new Date();
        adjustedEndDate.setHours(normalEndTime, 0, 0); // End time is 6:00 PM
        normalTime += (endDate - startDate) / (1000 * 60 * 60); // Time between start time and 6:00 PM
    }

    // Calculate overtime before 9:00 AM
    if (startDate.getHours() < normalStartTime) {
        const overtimeEndDate = new Date();
        overtimeEndDate.setHours(normalStartTime, 0, 0); // 9:00 AM
        overtime += (overtimeEndDate - startDate) / (1000 * 60 * 60); // Overtime before 9:00 AM
    }

    // Calculate overtime after 6:00 PM
    if (endDate.getHours() >= normalEndTime) {
        const overtimeStartDate = new Date();
        overtimeStartDate.setHours(normalEndTime, 0, 0); // 6:00 PM
        overtime += (endDate - overtimeStartDate) / (1000 * 60 * 60); // Overtime after 6:00 PM
    }

    // If the entire time is before 9:00 AM, consider all of it as overtime
    if (endDate.getHours() < normalStartTime) {
        overtime = totalTimeInHours; // All time is overtime if it's all before 9:00 AM
    }

    setNormalTime(normalTime.toFixed(2)); // Store normal time
    setOvertime(overtime.toFixed(2)); // Store overtime

    // Return total time, normal time, and overtime hours
    return { totalTime: totalTimeInHours.toFixed(2), normalTime: normalTime.toFixed(2), overtime: overtime.toFixed(2) };
};




  const handleInputChange = async (employeeId, date, start, end, totalTime, overtime) => {
    setTimesheetData((prevData) => {
      const newData = { ...prevData };
      newData[employeeId][date] = { startTime: start, endTime: end, totalTime, overtime };
      return newData;
    });

    await saveTimesheetDataToFirebase(employeeId, date, start, end, totalTime, overtime);
  };

  const saveTimesheetDataToFirebase = async (employeeId, date, start, end, totalTime, overtime) => {
    try {
      const timesheetDocRef = doc(db, "timesheets", employeeId);
      const timesheetData = await getDoc(timesheetDocRef);

      let updatedData = {};

      if (timesheetData.exists()) {
        updatedData = timesheetData.data().hours;
        updatedData[date] = { startTime: start, endTime: end, totalTime, overtime };
      } else {
        updatedData = { [date]: { startTime: start, endTime: end, totalTime, overtime } };
      }

      await setDoc(timesheetDocRef, { hours: updatedData });
      fetchTimesheetData();
    } catch (error) {
      console.error("Error saving timesheet data:", error);
    }
  };

  const fetchTimesheetData = async () => {
    try {
      const querySnapshot = await getDocs(collection(db, "timesheets"));
      const updatedTimesheetData = {};

      querySnapshot.forEach((doc) => {
        const data = doc.data();
        updatedTimesheetData[doc.id] = data.hours;
      });

      setTimesheetData(updatedTimesheetData);
    } catch (error) {
      console.error("Error fetching timesheet data:", error);
    }
  };

  const handleSetTime = (employeeId, date) => {
    setSelectedEmployee(employeeId);
    setSelectedDate(date);
    setModalOpen(true);
  };

  const handleCloseModal = () => {
    setModalOpen(false);
    setStartTime("");
    setEndTime("");
    setTotalTime(0);
    setOvertime(0); // Reset overtime
  };

  return (
    <div>
      <div>
        <h3>Time Adjustments</h3>
        <p>Normal Start Time: {timeAdjustments.normalStartTime}</p>
        <p>Normal End Time: {timeAdjustments.normalEndTime}</p>
      </div>

      <table border="1">
        <thead>
          <tr>
            <th>Employee</th>
            {weekDates.map((date) => (
              <th key={date}>{date}</th>
            ))}
          </tr>
        </thead>
        <tbody>
          {employees.map((employee) => (
            <tr key={employee.id}>
              <td>{employee.name}</td>
              {weekDates.map((date) => (
                <td key={date}>
                  <button onClick={() => handleSetTime(employee.id, date)}>Set Time</button>
                  <div>
                    {timesheetData[employee.id]?.[date] && (
                      <div>
                        <span>{`From ${timesheetData[employee.id][date].startTime} - ${timesheetData[employee.id][date].endTime}`}</span>
                        <br />
                        <strong>{`${timesheetData[employee.id][date].totalTime} hours`}</strong>
                        {timesheetData[employee.id][date].overtime > 0 && (
                          <div>
                            <strong>{`Overtime: ${timesheetData[employee.id][date].overtime} hours`}</strong>
                          </div>
                        )}
                      </div>
                    )}
                  </div>
                </td>
              ))}
            </tr>
          ))}
        </tbody>
      </table>

      {/* Modal for selecting time */}
      {modalOpen && (
        <div className="modal">
          <div className="modal-content">
            <h3>Set Work Hours</h3>
            <label>Start Time (24hr format):</label>
            <input
              type="time"
              value={startTime}
              onChange={(e) => {
                setStartTime(e.target.value);
                if (endTime) calculateTotalTime(e.target.value, endTime);
              }}
            />
            <br />
            <label>End Time (24hr format):</label>
            <input
              type="time"
              value={endTime}
              onChange={(e) => {
                setEndTime(e.target.value);
                if (startTime) calculateTotalTime(startTime, e.target.value);
              }}
            />
            <br />
            <strong>Total Time: {totalTime} hours</strong>
            {overtime > 0 && <div><strong>Overtime: {overtime} hours</strong></div>}
            <br />
            <button
              onClick={() => {
                handleInputChange(selectedEmployee, selectedDate, startTime, endTime, totalTime, overtime);
                handleCloseModal();
              }}
            >
              Save
            </button>
            <button onClick={handleCloseModal}>Cancel</button>
          </div>
        </div>
      )}
    </div>
  );
};

export default TimesheetList;
