import React, { useState } from "react";
import "./login.css"; // Import custom CSS for Login

const Login = ({ onLogin }) => {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [inputFilled, setInputFilled] = useState(false);

  const handleUsernameChange = (e) => {
    setUsername(e.target.value);
    setInputFilled(e.target.value.length > 0 || password.length > 0);  // Check if any input is filled
  };

  const handlePasswordChange = (e) => {
    setPassword(e.target.value);
    setInputFilled(username.length > 0 || e.target.value.length > 0);  // Check if any input is filled
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    onLogin(username, password);
    // You can keep or remove this, depending on the need to change cursor on form submit.
    document.body.style.cursor = 'url("/copper.png") 10 10, auto';  // Change cursor to copper on submit (optional)
  };

  return (
    <div className="login-container">
      <div className="login-box">
        <h2 className="login-title">Login</h2>
        <form onSubmit={handleSubmit}>
          <div className={`input-group ${inputFilled ? "input-filled" : ""}`}>
            <label htmlFor="username">Username</label>
            <input
              type="text"
              id="username"
              value={username}
              onChange={handleUsernameChange}
              placeholder="Enter your username"
              required
            />
          </div>
          <div className={`input-group ${inputFilled ? "input-filled" : ""}`}>
            <label htmlFor="password">Password</label>
            <input
              type="password"
              id="password"
              value={password}
              onChange={handlePasswordChange}
              placeholder="Enter your password"
              required
            />
          </div>
          <button type="submit" className="login-btn">Login</button>
        </form>
      </div>
    </div>
  );
};

export default Login;
